dc.ui.ShareDialog = dc.ui.Dialog.extend({

  id                    : 'share_dialog',
  className             : 'dialog',

  events : {
    'click .close':                            'close',
    'click .preview':                          'previewEmail',
    'click .next':                             '_nextStep',
    'click .previous':                         '_previousStep',
    'click .add_reviewer':                     '_showEnterEmail',
    'click .cancel_add':                       '_cancelAddReviewer',
    'click .minibutton.add':                   '_submitAddReviewer',
    'click .remove_reviewer':                  '_removeReviewer',
    'click .resend_reviewer':                  '_resendInstructions',
    'keypress .reviewer_management_email':     '_maybeAddReviewer',
    'keypress .reviewer_management_firstname': '_maybeAddReviewer',
    'keypress .reviewer_management_lastname':  '_maybeAddReviewer'
  },

  EMAIL_DIALOG_OPTIONS : {
    mode:        'short_prompt',
    description: _.t('reviewer_enter_email'),
    saveText:    _.t('next'),
    closeText:   _.t('cancel')
  },

  initialize : function(options) {
    _.bindAll(this, '_renderReviewers', '_refreshReviewers',
                    '_onAddSuccess', '_onAddError', '_onRemoveSuccess', '_onRemoveError',
                    '_showEnterEmail', '_nextStep');
    this.renderedAccounts = {};
    this._boundRender     = [];
    this.currentStep      = 1;
    this.newReviewers     = [];
    this.docs             = new dc.model.DocumentSet(options.docs);

    $(this.el).hide().empty();
    dc.ui.spinner.show();
    this.fetchReviewers();

    if (this.docs.all(function(doc) { return doc.get('reviewer_count') == 0; })) {
      this.renderEmailDialog();
    }
    dc.ui.Dialog.prototype.initialize.call(this, options);
  },

  render : function() {
    dc.ui.Dialog.prototype.render.call(this);
    this.setMode('share', 'dialog');
    this._container = this.$('.custom');
    this._container.html(JST['account/share_dialog']({
      'defaultAvatar' : dc.model.Account.prototype.DEFAULT_AVATAR,
      'docCount': this.docs.length
    }));
    this._next     = this.$('.next');
    this._previous = this.$('.previous');
    this._emailEl  = this.$('.reviewer_management input[name=email]');
    $(this.el).show();
    dc.ui.spinner.hide();
    this._setPlaceholders();
    this._setStep();
    this._enabledNextButton();
    this.center();
    return this;
  },

  // An array of all the reviewers in the selected documents (non-unique).
  reviewers : function() {
    return _.flatten(this.docs.map(function(doc) {
      return doc.reviewers.models;
    }));
  },

  // An array of all the accounts we need to email.
  accountsToEmail : function() {
    return _.uniq(_.filter(this.reviewers(), function(acc) {
      return acc.get('needsEmail');
    }));
  },

  // Run an iterator over each reviewer in our document list.
  eachReviewer : function(iterator) {
    _.each(this.reviewers(), _.bind(iterator, this));
  },

  // Return a list of documents that have reviewers (by id).
  docsForReviewers : function(reviewerIds) {
    return this.docs.select(function(doc) {
      return _.any(reviewerIds, function(id) {
        return doc.reviewers.get(id);
      });
    });
  },

  renderEmailDialog : function() {
    var docSize = this.docs.length;
    var title = _.t('share_x_documents', this.docs.length, dc.inflector.truncate(this.docs.first().get('title'), 35) );

    this.showingEmailDialog = true;
    dc.ui.spinner.hide();

    dc.ui.Dialog.prompt(title, '', _.bind(function(email) {
      this.showingEmailDialog = false;
      this._renderReviewers();
      this._showEnterEmail();
      this._focusEmail(email);
      this._submitAddReviewer();
      return true;
    }, this), this.EMAIL_DIALOG_OPTIONS);
  },

  managementError : function(message, warn) {
    this.$('.reviewer_management .error').toggleClass('error_white', !warn).text(message);
  },

  customMessage : function() {
    return dc.inflector.trim(this.$('.email_message').val());
  },

  previewEmail : function() {
    var docs = this.docsForReviewers(_.pluck(this.accountsToEmail(), 'id'));
    var ids  = _.map(docs, function(doc){ return 'document_ids[]=' + doc.id; }).join('&');
    window.open('/reviewers/preview_email?' + ids + '&message=' + encodeURIComponent(this.customMessage()));
  },

  // =======================
  // = Rendering Reviewers =
  // =======================

  fetchReviewers : function() {
    $.ajax({
      url : '/reviewers',
      type : 'GET',
      data : {document_ids: this.docs.pluck('id')},
      success: this._refreshReviewers,
      error : this._renderReviewers
    });
  },

  _refreshReviewers : function(resp) {
    if (this.showingEmailDialog) return;

    _.each(resp.reviewers, _.bind(function(reviewers, document_id) {
      this.docs.get(document_id).reviewers.reset(reviewers);
    }, this));
    this._renderReviewers();
  },

  _renderReviewers : function() {
    this.render();
    var views = [];
    this._countDocuments();
    this.renderedAccounts = {};
    this.eachReviewer(function(account) {
      var accountView = this._renderReviewer(account);
      if (accountView) views.push(accountView);
    });

    this.$('.account_list_content tr:not(.reviewer_management)').remove();
    this.$('.account_list_content').prepend(views);
    this.$('.document_reviewers_empty').toggle(!_.keys(this.renderedAccounts).length);
    this._cancelAddReviewer();
  },

  _setPlaceholders : function() {
    this.$('input[name=first_name], input[name=last_name]').placeholder();
    this._emailEl.placeholder();
  },

  _countDocuments : function() {
    var counts = this.accountDocumentCounts = {};
    this.eachReviewer(function(account) {
      counts[account.id] = (counts[account.id] || 0) + 1;
    });
  },

  _renderReviewer : function(account) {
    if (this.renderedAccounts[account.id]) return;

    var view = this.renderedAccounts[account.id] = new dc.ui.AccountView({
      model  : account,
      kind   : 'reviewer',
      dialog : this
    });
    this._rerenderReviewer(account);

    account.bind('change:needsEmail', _.bind(this._enabledNextButton, this));
    account.bind('change:needsEmail', _.bind(this._rerenderReviewer, this, account));

    this._observeReviewer(account, view);

    return view.el;
  },

  _rerenderReviewer : function(account) {
    this.renderedAccounts[account.id].render('display', {
      documentCount  : this.accountDocumentCounts[account.id],
      documentsCount : this.docs.length
    });
  },

  _observeReviewer : function(account, view) {
    account.unbind('change', view._boundRender);
    if (this._boundRender[account.id]) account.unbind('change', this._boundRender[account.id]);
    this._boundRender[account.id] = _.bind(this._rerenderReviewer, this, account);
    account.bind('change', this._boundRender[account.id]);
  },

  // ==================================
  // = Add/Remove Reviewer Management =
  // ==================================

  _showEnterEmail : function() {
    if (this.showingManagement) return this._submitAddReviewer(this._showEnterEmail);

    this.showingManagement = true;
    this.$('.reviewer_management').show();
    this.$('.document_reviewers_empty').hide();
    this._focusEmail();
    this._enabledNextButton();
  },

  _focusEmail : function(email) {
    var $reviewers = this.$('.document_reviewers');
    $reviewers.attr('scrollTop', $reviewers.attr("scrollHeight")+100);
    this._emailEl.focus();

    if (email)  this._emailEl.val(email);
    var email = this._emailEl.val();
    if (!email) this.managementError( _.t('please_enter_email') );
  },

  _cancelAddReviewer : function() {
    this.showingManagement = false;
    this.$('.reviewer_management').hide();
    this.$('.reviewer_management input').val('');
    this.$('.document_reviewers_empty').toggle(!_.keys(this.renderedAccounts).length);
    this._enabledNextButton();
  },

  // Try to add reviewer on `enter` key.
  _maybeAddReviewer : function(e) {
    if (e.which == 13) this._submitAddReviewer();
  },

  _submitAddReviewer : function(callback, dismissEmpty) {
    var email = this._emailEl.val();
    if (!email.length && this.accountsToEmail().length && dismissEmpty) {
      this._cancelAddReviewer();
      return callback();
    }
    if (!dc.app.validator.check(email, 'email')) {
      this._focusEmail();
      this.managementError(_.t('please_enter_valid_email'), true);
      return false;
    }

    this.showSpinner();
    $.ajax({
      url : '/reviewers',
      type : 'POST',
      data : {
        email         : dc.inflector.trim(email),
        first_name    : dc.inflector.trim(this.$('.reviewer_management input[name=first_name]').val()),
        last_name     : dc.inflector.trim(this.$('.reviewer_management input[name=last_name]').val()),
        document_ids  : this.docs.pluck('id')
      },
      success: _.bind(function(resp) { this._onAddSuccess(resp, callback); }, this),
      error : this._onAddError
    });
  },

  _onAddSuccess : function(resp, callback) {
    _.each(resp.documents, function(doc) {
      Documents.get(doc.id).set(doc);
    });
    var newAccount = new dc.model.Account(resp.account);
    this.docs.each(function(doc) {
      var account = doc.reviewers.get(newAccount.id);
      if (!account) doc.reviewers.add(newAccount);
      (account || newAccount).set({needsEmail: true});
    });
    this.showingManagement = false;
    this._renderReviewers();
    if (callback) callback();
    this._enabledNextButton();
  },

  _onAddError : function(resp) {
    var status = resp.status;

    resp = JSON.parse(resp.responseText);
    if (resp.errors && _.any(resp.errors, function(error) {
      error = error.toLowerCase();
      return error.indexOf("first name") != -1 || error.indexOf("last name") != -1;
    })) {
      this._showReviewerNameInputs();
      this.managementError( _.t('reviewer_name') );
    } else if (resp.errors) {
      this.managementError(resp.errors[0], true);
    } else if (status == 403) {
      this.error( _.t('reviewer_add_permission_denied'));
    } else {
      this.managementError( _.t('reviewer_enter_email'), true);
    }
    this.hideSpinner();
    this._focusEmail();
    this._enabledNextButton();
  },

  _showReviewerNameInputs : function() {
    this.$('input[name=first_name], input[name=last_name]').show();
    this.$('.enter_full_name_label').show();
    this.$('.enter_email_label').hide();
    this._setPlaceholders();
  },

  _removeReviewer : function(e) {
    this.showSpinner();
    var accountId    = parseInt($(e.target).attr('data-account-id'), 10);
    var account      = _.detect(this.reviewers(), function(acc){ return acc.id == accountId; });
    var reviewerDocs = this.docsForReviewers([account.id]);
    var documentIds  = _.pluck(reviewerDocs, 'id');

    $.ajax({
      url : '/reviewers/' + accountId,
      type : 'DELETE',
      data : {
        account_id : accountId,
        document_ids : documentIds
      },
      success: _.bind(function(resp) {
        this._onRemoveSuccess(resp, documentIds, account);
      }, this),
      error : this._onRemoveError
    });
    this._enabledNextButton();
  },

  _onRemoveSuccess : function(resp, documentIds, account) {
    _.each(resp, function(doc) {
      Documents.get(doc.id).set(doc);
    });
    this.docs.each(_.bind(function(doc) {
      if (_.contains(documentIds, doc.id)) {
        doc.reviewers.remove(account);
      }
    }, this));
    dc.ui.notifier.show({
      text      : _.t('x_is_no_longer_a_reviewer_on_x', account.get('email'), documentIds.length ),
      duration  : 5000,
      mode      : 'info'
    });
    this._renderReviewers();
    this._enabledNextButton();
  },

  _onRemoveError : function(resp) {
    this.hideSpinner();
     if (resp.status == 403) {
       this.error( _.t('reviewer_remove_permission_denied') );
    } else {
      this.error( _.t('reviewer_remove_error') );
    }
    this._enabledNextButton();
  },

  _resendInstructions : function(e) {
    var accountId = parseInt($(e.target).attr('data-account-id'), 10);
    this.eachReviewer(function(account) {
      if (account.id == accountId) account.set({needsEmail: true});
    });

    this._enabledNextButton();
    this._nextStep();
  },

  // ====================
  // = Step Two: Emails =
  // ====================

  _setEmailDescription : function() {
    var emails = dc.inflector.commify(
      _.map( this.accountsToEmail(),
             function(a){ return a.fullName(); }
           )
      ,{conjunction: 'and'});
    this.$('.email_description').html( _.t('reviewer_email_message', emails ) );
  },

  _sendInstructions : function() {
    var accounts = this.accountsToEmail();
    this.showSpinner();

    this._next.setMode('not', 'enabled');
    this._next.html( _.t('sending') );

    var accountIds  = _.pluck(accounts, 'id');
    var documents   = this.docsForReviewers(accountIds);
    var documentIds = _.pluck(documents, 'id');
    var message     = this.customMessage();

    $.ajax({
      url : '/reviewers/send_email',
      type : 'POST',
      data : {account_ids : accountIds, document_ids : documentIds, message : message},
      success: _.bind(function(resp) {
        var account =  (accounts.length == 1 ? accounts[0].get('email') : accounts.length + ' people' );

        var text = (documentIds.length == 1 ?
                    _.t('reviewing_instructions_single_sent_to',
                        dc.inflector.truncate(this.docs.get(documentIds[0]).get('title'), 30), account ) :
                    _.t('reviewing_instructions_multiple_sent_to', documentIds.length, account )
                   );

        dc.ui.notifier.show({
          text     : text,
          duration : 5000,
          mode     : 'info'
        });

        this.hideSpinner();
        this.close();
      }, this),
      error : _.bind(function(resp) {
        this.hideSpinner();
        this.error( _.t('reviewing_instructions_send_failure') );
        this._setStep();
        this._enabledNextButton();
      }, this)
    });
  },

  // =========
  // = Steps =
  // =========

  _enabledNextButton : function() {
    this._nextEnabled = this.accountsToEmail().length || this.showingManagement;
    this._next.setMode(this._nextEnabled ? 'is' : 'not', 'enabled');
  },

  _setStep : function() {
    var title = this._displayTitle();
    this.title(title);
    var last = this.currentStep == 2;

    this._next.html(last ? _.t('send') : _.t('next') + ' &raquo;');
    this.setMode('p' + this.currentStep, 'step');
    if (last) this._setEmailDescription();
  },

  _displayTitle : function() {
    if (this.currentStep == 1) {
      return _.t("share_x_documents", this.docs.length, dc.inflector.truncate(this.docs.first().get('title'), 30) );
    } else {
      var accounts = this.accountsToEmail();
      return _.t('reviewer_email_instructions', accounts.length, accounts[0].fullName() );
    }
  },

  _nextStep : function() {
    if (!this._nextEnabled) return;
    if (this.showingManagement) return this._submitAddReviewer(this._nextStep, true);
    if (this.currentStep >= 2) {
      this._sendInstructions();
    } else {
      this.currentStep += 1;
      this._setStep();
    }
  },

  _previousStep : function() {
    if (this.currentStep > 1) this.currentStep -= 1;
    this._setStep();
  }

});

dc.ui.CreateGroupDialog = dc.ui.Dialog.extend({

  id                : 'create_group_dialog',
  className         : 'dialog new_css_dialog',
  mode              : 'create',

  dataEvents : {
      'click .cancel'     : 'close',
      'click .ok'         : 'saveAndClose',
      'focus input'       : '_addFocus',
      'focus textarea'    : '_addFocus',
      'blur input'        : '_removeFocus',
      'blur textarea'     : '_removeFocus'
  },


  constructor : function(group) {
    this.model      = group;
    this.events     = _.extend({}, this.events, this.dataEvents);
    this._mainJST   = JST['document/create_group_dialog'];
    _.bindAll(this, 'render', 'saveAndClose', 'showErrors', 'updateGroupName');
    dc.ui.Dialog.call(this, {mode : 'custom', title : _.t('create_group'), saveText : _.t('save') });

    //Set mode (create or edit)
    this.mode = this.model.id == null ? 'create' : 'edit';

    this.render();

    $(document.body).append(this.el);
  },


  render : function() {
    //Base dialog object needs
    dc.ui.Dialog.prototype.render.call(this);
    this._container = this.$('.custom');

    //Main template
    this._container.html(this._mainJST({
        name:      this.model.get('name'),
        extension: this.model.get('extension'),
        mode:      this.mode
    }));

    $('.typeahead').typeahead({
          hint: true,
          highlight: true,
          minLength: 0
        },
        {
          name: 'templateArray',
          displayKey: 'value',
          source: FuncUtils.substringMatcher(dc.app.editor.templateArray)
        }
    );

    $('.typeahead').bind('typeahead:selected', this.updateGroupName);

    return this;
  },


  saveAndClose : function(success) {
    _thisDialog = this;

    //Clear error class from all inputs
    $('input').removeClass('error');

    //Validate that group name is not blank.
    if( _thisDialog.$('#group_name').val() == null || _thisDialog.$('#group_name').val().length == 0 ){
      _thisDialog.$('#group_name').addClass('error');
      return _thisDialog.error(_.t('blank_field_error'));
    }

    //Push template name to model
    _thisDialog.model.set({
      name: _thisDialog.$('#group_name').val(),
      extension: _thisDialog.$('#extension').val()
    });

    //If creating, also push template data to model (If template entered)
    if( this.mode == 'create' ) {
      var templateName = _thisDialog.$('#template_name').val();

      if( templateName && templateName.length > 0 ) {
        var templateIDs = dc.app.editor.getIDsForTemplateString(templateName);

        //Error if template name fails to match
        if (templateIDs[0] == null || ((templateName.indexOf('::') > -1) && templateIDs[1] == null)) {
          _thisDialog.$('#template_name').addClass('error');
          return _thisDialog.error(_.t('template_name_invalid'));
        }

        _thisDialog.model.set({
          template_id: templateIDs[0],
          subtemplate_id: templateIDs[1]
        });
      }
    }

    //Trigger save
    _thisDialog.model.save({}, {success: function(){ _thisDialog.close(); }});
  },


  //Handler for errors returned from model validation
  showErrors: function(model, errors) {
    //Handle first error only
    if(errors[0].class == 'name'){ this.$('#template_name').addClass('error'); }

    return this.error(errors[0].message);
  },


  //Update group name from template chosen
  updateGroupName: function(){
    var _template = this.$('#template_name').val();
    if( _template.indexOf('::') > -1 ){ _template = _template.substr(0, _template.indexOf('::')); }
    this.$('#group_name').val(_template);
  }

}, {

  // This static method is used for conveniently opening the dialog for
  // any selected template.
  open : function(group) {
    new dc.ui.CreateGroupDialog(group);
  }

});

// A tile view for previewing a Document in a listing.
dc.ui.Document = Backbone.View.extend({
  // Number of pages to show at a time.
  PAGE_LIMIT : 50,

  // To display if the document failed to upload.
  ERROR_MESSAGE : '<span class="interface">' + _.t('document_error_message',
                                                   '<span class="text_link troubleshoot">', '</span>',
                                                   '<span class="text_link contact_us">',   '</span>' ) +
    '</span>',

  className : 'document',

  events : {
    'click .doc_title'              : 'select',
    'contextmenu .doc_title'        : 'showMenu',
    'dblclick .doc_title'           : 'viewDocument',
    'click .icon.doc'               : 'select',
    'contextmenu .icon.doc'         : 'showMenu',
    'dblclick .icon.doc'            : 'viewDocument',
    'click .title .edit_glyph'      : 'openDialog',
    'click .datalines .edit_glyph'  : 'openDataDialog',
    'click .title .published'       : 'viewPublishedDocuments',
    'click .page_icon'              : '_openPage',
    'click .reviewer_count'         : '_openShareDialog',
    'click .occurrence'             : '_openPage',
    'click .mention b'              : '_openPage',
    'click .pages .cancel_search'   : '_hidePages',
    'click .page_count'             : '_togglePageImages',
    'click .search_account'         : 'searchAccount',
    'click .search_group'           : 'searchOrganization',
    'click .search_source'          : 'searchSource',
    'click .change_publish_at'      : 'editPublishAt',
    'click .troubleshoot'           : 'openTroubleshooting',
    'click .contact_us'             : 'openContactUs',
    'click .open_pages'             : 'openPagesInViewer',
    'click .show_mentions'          : 'fetchAllMentions',
    'click .page_list .left'        : 'previousPage',
    'click .page_list .right'       : 'nextPage',
    'click .data_item'              : 'searchData'
  },

  // Takes a document model and sets up listeners on the document model,
  // its notes, and pageEntities.
  constructor : function(options) {
    Backbone.View.call(this, options);
    this.el.id = 'document_' + this.model.id;
    this._currentPage = 0;
    this._showingPages = false;
    this.setMode(this.model.get('annotation_count') ? 'owns' : 'no', 'notes');
    _.bindAll(this, '_onDocumentChange', '_onDrop', '_addNote', '_renderNotes',
      '_renderPages', '_renderEntities', '_setSelected', 'viewDocuments',
      'viewPublishedDocuments', 'openDialog', 'setAccessLevelAll', 'viewEntities',
      'hideNotes', 'viewPages', 'viewChosenPages', 'deleteDocuments',
      'removeFromProject', '_openShareDialog', 'openDataDialog', 'focus');
    this.model.bind('change', this._onDocumentChange);
    this.model.bind('change:selected', this._setSelected);
    this.model.bind('focus', this.focus);
    this.model.bind('view:pages', this.viewPages);
    this.model.bind('notes:hide', this.hideNotes);
    this.model.notes.bind('add', this._addNote);
    this.model.notes.bind('reset', this._renderNotes);
    this.model.entities.bind('load', this._renderEntities);
    this.model.pageEntities.bind('reset', this._renderPages);
  },

  // Render the document tile, setting modes, selected state, and attaches drag events.
  render : function(options) {
    options || (options = {});
    var me = this;
    var title = this.model.get('title');
    var data = _.extend(this.model.toJSON(), {
      model         : this.model,
      created_at    : this.model.get('created_at').replace(/\s/g, '&nbsp;'),
      icon          : this._iconAttributes(),
      thumbnail_url : this._thumbnailURL(),
      data          : this.model.sortedData(),
      status_text   : dc.app.workspace.documentList.DOC_STATUS[parseInt(this.model.get('status')) - 1].toUpperCase(),
      admin_view    : dc.account.isAdmin()
    });
    if (dc.app.paginator && dc.app.paginator.mini) data.title = dc.inflector.truncateWords(data.title, 50);
    $(this.el).html(JST['document/tile'](data));
    this._displayDescription();
    if (dc.account) this.$('.doc.icon').draggable({ghost : true, onDrop : this._onDrop});
    this.notesEl = this.$('.notes');
    this.entitiesEl = this.$('.entities');
    this.pagesEl = this.$('.pages');
    this.entitiesView = new dc.ui.SparkEntities({model: this.model, parent: this, container: this.$('.entities')});
    this.model.notes.each(function(note){ me._addNote(note); });
    if (options.notes && this.model.hasLoadedNotes()) this.setMode('has', 'notes');
    this.setMode('claimed', this.model.get('has_current_claim') ? 'yes' : 'no');
    this.setMode(this.model.allowedToEdit() ? 'is' : 'not', 'editable');
    this._setSelected();
    return this;
  },

  // Desktop-style selection.
  select : function(e) {
    e.preventDefault();
    if (!this.model.get('selectable')) return;
    var alreadySelected =  this.model.get('selected');
    var hk = dc.app.hotkeys;
    var anchor = Documents.firstSelection || Documents.selected()[0];
    if (hk.command || hk.control) {
      // Toggle.
      this.model.set({selected : !alreadySelected});
    } else if (hk.shift && anchor) {
      // Range.
      var idx = Documents.indexOf(this.model), aidx = Documents.indexOf(anchor);
      var start = Math.min(idx, aidx), end = Math.max(idx, aidx);
      Documents.each(function(doc, index) {
        doc.set({selected : index >= start && index <= end});
      });
    } else {
      // Regular.
      Documents.deselectAll();
      this.model.set({selected : true});
    }
  },

  // If the document is published, open the remote url, otherwise open
  // the local DocumentCloud viewer.
  viewDocument : function(e) {
    this.model.openAppropriateVersion();
    return false;
  },

  // Opens the remote document viewer.
  viewPublishedDocuments : function() {
    var docs = Documents.chosen(this.model);
    if (!docs.length) return;
    _.each(docs, function(doc){
      if (doc.isPublished()) doc.openPublishedViewer();
    });
  },

  // Opens the local document viewer.
  viewDocuments : function() {
    var docs = Documents.chosen(this.model);
    if (!docs.length) return;
    _.each(docs, function(doc){ doc.openViewer(); });
  },

  viewPDF : function() {
    this.model.openPDF();
  },

  viewFullText : function() {
    this.model.openText();
  },

  // Old implementation of viewEntities:
  // dc.app.searcher.viewEntities(Documents.chosen(this.model));

  viewEntities : function() {
    var docs = Documents.chosen(this.model);
    dc.app.paginator.ensureRows(function(){
      dc.model.EntitySet.populateDocuments(docs);
    }, docs[0]);
  },

  hideNotes : function() {
    this.setMode(this.model.hasNotes() ? 'owns' : 'no', 'notes');
  },

  downloadViewer : function() {
    if (this.checkBusy()) return;
    this.model.downloadViewer();
  },

  // Open an edit dialog for this document.
  openDialog : function(e) {
    if (!(this.modes.editable == 'is')) return;
    if (this.model.checkBusy()) return;
    dc.ui.DocumentDialog.open(this.model);
  },

  // Open a dialog to edit the document's data.
  openDataDialog : function(e) {
    if (!(this.modes.editable == 'is')) return;
    if (this.model.checkBusy()) return;
    dc.ui.DocumentDataDialog.open(this.model);
  },

  openPagesInViewer : function() {
    this.model.openViewer('#pages');
  },

  fetchAllMentions : function() {
    this.model.fetchMentions(dc.app.searchBox.value());
  },

  previousPage : function() {
    this._currentPage--;
    this.viewPages();
  },

  nextPage : function() {
    this._currentPage++;
    this.viewPages();
  },

  // Show the notes attached to the document model. Requires a server fetch.
  toggleNotes : function(e) {
    e.stopPropagation();
    var next = _.bind(function() {
      var model = Documents.get(this.model.id);
      if (this.modes.notes == 'has') return this.setMode('owns', 'notes');
      if (model.checkBusy()) return;
      if (model.hasLoadedNotes()) {
        this._renderNotes();
        return this.setMode('has', 'notes');
      }
      dc.ui.spinner.show('loading notes');
      model.notes.fetch({
        reset: true,
        success : function() {
          dc.ui.spinner.hide();
          window.scroll(0, $('#document_' + model.id).offset().top - 100);
      }});
    }, this);
    dc.app.paginator.mini ? dc.app.paginator.toggleSize(next, this.model) : next();
  },

  // Show thumbnails of the pages in the document. Paged using `this._currentPage`.
  viewPages : function() {
    this._showingPages = true;
    this.model.ensurePerPageNoteCounts(_.bind(function(noteCounts) {
      var start = (this._currentPage * this.PAGE_LIMIT) + 1;
      var total = this.model.get('page_count');
      this.pagesEl.html(JST['document/page_images']({
        doc     : this.model,
        start   : start,
        end     : Math.min(start + this.PAGE_LIMIT - 1, total),
        total   : total,
        limit   : this.PAGE_LIMIT,
        notes   : noteCounts
      }));
    }, this));
  },

  // Document context menu item which triggers the `view:pages` event on each selected
  // document. This will call `viewPages`, which displays page thumbnails below
  // the document tile.
  viewChosenPages : function() {
    var docs = Documents.chosen(this.model);
    dc.app.paginator.ensureRows(function() {
      _.each(docs, function(doc){
        if (doc = Documents.get(doc.id)) doc.trigger('view:pages');
      });
    }, docs[0]);
  },

  // Document context menu item used to delete multiple selected documents.
  deleteDocuments : function() {
    Documents.verifyDestroy(Documents.chosen(this.model));
  },

  // Document context menu item used to modify multiple selected documents.
  removeFromProject : function() {
    Projects.firstSelected().removeDocuments(Documents.chosen(this.model));
  },

  // Clicking on the contributor's name in the document tile searches for other
  // documents by this account.
  searchAccount : function() {
    dc.app.searcher.addToSearch(_.t('account'), this.model.get('account_slug'));
  },

  // Clicking on the organization's name in the document tile searches for other
  // documents contributed by this organization.
  searchOrganization : function() {
    dc.app.searcher.addToSearch(_.t('group'), this.model.get('organization_slug'));
  },

  // Clicking on the source in the document tile searches for other documents
  // from this source.
  searchSource : function() {
    dc.app.searcher.addToSearch(_.t('source'), this.model.get('source').replace(/"/g, '\\"'));
  },

  searchData : function(e) {
    var el    = $(e.currentTarget);
    var key   = el.find('.data_key').text().replace(/:$/, '');
    var value = el.find('.data_value').text();
    dc.app.searcher.toggleSearch(key, value);
  },

  // Context menu item opens access level dialog for a single document.
  editAccessLevel : function() {
    Documents.editAccess([this.model]);
  },

  // Context menu item opens access level dialog for multiple selected documents.
  setAccessLevelAll : function() {
    Documents.editAccess(Documents.chosen(this.model));
  },

  // Opens dialog to change `publish_at` field on document model.
  editPublishAt : function() {
    new dc.ui.PublicationDateDialog([this.model]);
  },

  // Focus by jumping to the top of the view.
  focus : function() {
    window.scroll(0, $(this.el).offset().top - 100);
  },

  // Documents that have failed processing show a link to this help page.
  openTroubleshooting : function() {
    dc.app.workspace.help.openPage('troubleshooting');
  },

  // Documents that have failed processing show a link to this contact dialog.
  openContactUs : function() {
    dc.ui.Dialog.contact();
  },

  // Opens the Document Reviewers dialog for a single document.
  _openShareDialog : function() {
    dc.app.shareDialog = new dc.ui.ShareDialog({
      docs: [this.model],
      mode: 'custom'
    });
  },

  // Shows the context menu for the document tile. Multiple documents can be selected
  // and have the context menu items apply to them.
  showMenu : function(e) {
    e.preventDefault();
    var menu = dc.ui.Document.sharedMenu || (dc.ui.Document.sharedMenu = new dc.ui.Menu({
      id : 'document_menu',
      standalone : true
    }));
    var count = Documents.chosen(this.model).length;
    if (!count) return;

    menu.clear();
    var items = [{title : _.t('open'), onClick: this.viewDocuments}];
    if (this.model.isPublished()) {
      items.push({
        title   : _.t('open_published_version'),
        onClick : this.viewPublishedDocuments
      });
    }
    items.push({title : _.t('view_entities'), onClick: this.viewEntities});
    items.push({title : _.t('view_pages'), onClick: this.viewChosenPages});
    if (this.model.allowedToEdit()) {
      items = items.concat([
        {title : _.t('edit_document_information'), onClick: this.openDialog},
        {title : _.t('edit_document_data'),        onClick: this.openDataDialog},
        {title : _.t('set_access'),                onClick: this.setAccessLevelAll},
        {title : _.t('delete_documents', count),    onClick: this.deleteDocuments,
                                              attrs : {'class' : 'warn'}}
      ]);
    }
    if (Projects.firstSelected()) {
      var index = items.length - (items[items.length - 1].onClick == this.deleteDocuments ? 1 : 0);
      items.splice(index, 0, {title : _.t('remove_from_project'), onClick: this.removeFromProject});
    }
    menu.addItems(items);
    menu.render().open().content.css({top : e.pageY, left : e.pageX});
  },

  // Helper method for setting which action icons appear next to the document tile.
  _iconAttributes : function() {
    var access = this.model.get('access');
    var base = 'icon main_icon document_tool ';
    switch (access) {
      case dc.access.PENDING:
        return {'class' : base + 'spinner',    title : _.t('uploading') + ' ...' };
      case dc.access.ERROR:
        return {'class' : base + 'alert_gray', title : _.t('broken_document') };
      case dc.access.ORGANIZATION:
        return {'class' : base + 'lock',       title : _.t('private_to', (dc.account ?
                                                       dc.account.organization().get('name') :
                                                      'your organization') )};
      default:
        if( this.model.get('has_current_claim')) {
           return {'class' : base + 'lock',       title : _.t('claimed')};
        }
        return {'class' : base + 'hidden', iconless: true};
    }
  },

  // Helper method for getting the right document tile icon URL, based on
  // failure status of the document model.
  _thumbnailURL : function() {
    var access = this.model.get('access');
    switch (access) {
      case dc.access.PENDING: return '/images/embed/documents/processing.png';
      case dc.access.ERROR:   return '/images/embed/documents/failed.png';
      default:                return this.model.get('thumbnail_url');
    }
  },

  // HTML descriptions need to be sanitized for the workspace.
  _displayDescription : function() {
    var el = this.$('.description_text');
    if (this.model.get('access') == dc.access.ERROR) return el.html(this.ERROR_MESSAGE);
    el.text(dc.inflector.stripTags(this.model.get('description') || ''));
  },

  // Trigger-based method which shows selection highlight on document tile.
  _setSelected : function() {
    var sel = this.model.get('selected');
    this.setMode(sel ? 'is' : 'not', 'selected');
  },

  // Trigger-based method which re-renders the tile if any properties on the model
  // have changed.
  _onDocumentChange : function() {
    if (this.model.hasChanged('selected')) return;
    this.render();
  },

  // Render each of a document's notes, which have already been fetched.
  _addNote : function(note) {
    var noteView = new dc.ui.Note({
      model : note,
      collection : this.model.notes
    });
    this.notesEl.append(noteView.render().el);
    noteView.center();
  },

  // Re-renders the notes when the notes are refreshed.
  _renderNotes : function() {
    this.notesEl.empty();
    this.model.notes.each(this._addNote);
    this.setMode(this.model.ignoreNotes ? 'owns' : 'has', 'notes');
  },

  // Re-renders the entities when the entities are refreshed.
  _renderEntities : function() {
    if (this.model.entities.length) {
      this.entitiesView.show();
    } else {
      dc.ui.Dialog.alert( _.t('has_no_entities', this.model.get('title') ) );
    }
  },

  // Clicking on the page counts in the tile opens up the page thumbnails below
  // the document tile.
  _togglePageImages : function() {
    if (this._showingPages) {
      this._hidePages();
    } else {
      this.viewPages();
    }
  },

  // Clicking on entities shows which pages they are found on, and jumps to this
  // view.
  _renderPages : function() {
    this._showingPages = false;
    this.pagesEl.html(JST['document/pages']({doc : this.model}));
    this.focus();
  },

  // Hiding the entity search locations.
  _hidePages : function() {
    this._showingPages = false;
    this._currentPage = 0;
    this.pagesEl.html('');
  },

  // Clicking on either a page thumbnail or an entity in the document tile opens
  // up the document viewer, set to that page or entity.
  _openPage : function(e) {
    var el   = $(e.target).closest('.page');
    var page = el.attr('data-page');
    var id   = el.attr('data-id');
    if (el.hasClass('mention')) {
      var text = el.find('b').text();
      this.model.openViewer('#search/p' + page + '/' + encodeURIComponent(text));
    } else if (id) {
      this.model.openEntity(id, el.attr('data-offset'));
    } else {
      this.model.openViewer('#document/p' + page);
    }
  },

  // When the document is dropped onto a project, add it to the project.
  _onDrop : function(e) {
    var docs = [this.model];
    var selected = Documents.selected();
    if (selected.length && _.include(selected, this.model)) docs = selected;
    var x = e.pageX, y = e.pageY;
    $('#organizer .project').each(function() {
      var top = $(this).offset().top, left = $(this).offset().left;
      var right = left + $(this).outerWidth(), bottom = top + $(this).outerHeight();
      if (left < x && right > x && top < y && bottom > y) {
        var project = Projects.get($(this).attr('data-project-cid'));
        if (project) project.addDocuments(docs);
        return false;
      }
    });
  }

});

dc.ui.DocumentDataDialog = dc.ui.Dialog.extend({

  className : 'dialog datalog',

  dataEvents : {
    'click .minus':       '_removeDatum',
    'click .plus':        '_addDatum',
    'click .remove_all':  '_removeAll'
  },

  constructor : function(docs) {
    this.events       = _.extend({}, this.events, this.dataEvents);
    this.docs         = docs;
    this.multiple     = docs.length > 1;
    this.originalData = Documents.sharedData(this.docs);
    this._rowTemplate = JST['document/data_dialog_row'];
    dc.ui.Dialog.call(this, {mode : 'custom', title : _.t('edit_data_for',this._title()), saveText : _.t('save') });
    this.render();
    $(document.body).append(this.el);
  },

  render : function() {
    dc.ui.Dialog.prototype.render.call(this);
    var data = Documents.sortData(this.originalData);
    var removeAll = _.any(this.docs, function(doc){ return !_.isEmpty(doc.get('data')); });
    this._container = this.$('.custom');
    this._container.html(JST['document/data_dialog']({
      documentCount : this.docs.length,
      removeAll     : removeAll,
      data          : data
    }));
    this.checkNoData();
    return this;
  },

  checkNoData : function() {
    if (!this.$('.data_row').length) {
      var container = this._container.find('.rows');
      var template  =
      container.html(
        this._rowTemplate({key: '', value: '', minus: false}) +
        this._rowTemplate({key: '', value: '', minus: true}) +
        this._rowTemplate({key: '', value: '', minus: true})
      );
    }
  },

  serialize : function() {
    var data = {};
    _.each(this.$('.data_row'), function(row) {
      data[$(row).find('.key').val()] = $(row).find('.value').val();
    });
    return data;
  },

  confirm : function() {
    var data = this.serialize();
    var forbidden = _.detect(_.keys(data), function(key){ return _.include(dc.searchPrefixes, key.toLowerCase()); });
    if (forbidden) {
      this.error( _.t('bad_data_key', forbidden ) );
    } else {
      var toRemove = _.without.apply(_, [_.keys(this.originalData)].concat(_.keys(data)));
      _.each(this.docs, function(doc){ doc.mergeData(data, toRemove); });
      this.close();
    }
  },

  _removeDatum : function(e) {
    $(e.target).closest('.data_row').remove();
    this.checkNoData();
  },

  _addDatum : function(e) {
    var newRow = $(this._rowTemplate({key: '', value: '', minus: true}));
    $(e.target).closest('.data_row').after(newRow);
    newRow.find('.key').focus();
    this.checkNoData();
  },

  _removeAll : function() {
    this.close();
    var docs = this.docs;
    var message = _.t('confirm_remove_all_data', this._title() );
    dc.ui.Dialog.confirm(message, function() {
      _.each(docs, function(doc){ doc.save({data: {}}); });
      return true;
    });
    this.close();
  },

  // Sets the dialog title to include the number of documents or title of
  // the single document being edited.
  _title : function() {
    if (this.multiple) return _.t('x_documents', this.docs.length );
    return '"' + dc.inflector.truncate(this.docs[0].get('title'), 25) + '"';
  },

  _returnCloses : function() {
    return true;
  }

}, {

  // This static method is used for conveniently opening the dialog for
  // any selected documents.
  open : function(doc) {
    var docs = Documents.chosen(doc);
    if (!docs.length) return;
    if (!Documents.allowedToEdit(docs)) return;
    new dc.ui.DocumentDataDialog(docs);
  }

});

// Document dialog shows all user-editable attributes. Can be applied on multiple
// documents at once, sending a `Backbone.Model#save` per-document.
dc.ui.DocumentDialog = dc.ui.Dialog.extend({

  ATTRIBUTES : ['title', 'source', 'description', 'related_article', 'study', 'access', 'language', 'repository_id'],

  id        : 'edit_document_dialog',
  className : 'dialog docalog',
  repoCollection : null,

  events : {
    'click .cancel'     : 'close',
    'click .ok'         : 'save',
    'focus input'       : '_addFocus',
    'focus textarea'    : '_addFocus',
    'blur input'        : '_removeFocus',
    'blur textarea'     : '_removeFocus',
    'click .delete'     : 'destroy',
    'change .attribute' : '_markChanged'
  },
  
  // Takes multiple documents as a collection, since any changes are applied to
  // all documents.
  constructor : function(docs) {
    _.bindAll(this, 'render');
    this.docs = docs;
    this.multiple = docs.length > 1;
    var title = _.t('edit_x', this._title() );
    dc.ui.Dialog.call(this, {mode : 'custom', title : title, editor : true});

    //Populate repos
    this.repoCollection = new dc.model.Repositories();
    this.repoCollection.fetch({data:{}, success: this.render, error: this.error});

    $(document.body).append(this.el);
  },

  // Renders a single form for multiple documents.
  render : function() {
    dc.ui.Dialog.prototype.render.call(this);
    this._container = this.$('.custom');
    this._container.html(JST['document/document_dialog']({
      docs     : this.docs, 
      multiple : this.multiple
    }));

    //Repo dropdown
    $('select[name=repo]').append($('<option>', { value : null }).text('Public'));
    $.each(this.repoCollection.models, function(key, value) {
      $('select[name=repo]')
          .append($('<option>', { value : value.attributes.id })
              .text(value.attributes.repo_name));
    });

    $('select[name=language]').val( this.docs[0].get('language') );
    var attrs = this._sharedAttributes();
    attrs['access'] = attrs['access'] || dc.access.PRIVATE;
    _.each(this.ATTRIBUTES, _.bind(function(attr) {
      $('#document_edit_' + attr).val(attrs[attr] || '');
    }, this));
    this.center();
    return this;
  },

  // For each attributes, if different any of the original attributes of each document,
  // saves the new attribute to each document using `Backbone.Model#save`.
  save : function() {
    var original = this._sharedAttributes();
    var changes = {};
    _.each(this.ATTRIBUTES, _.bind(function(attr) {
      var el = this.$('#document_edit_' + attr);
      if (!el.length) return;
      var next = el.val();
      if (attr == 'access') next = parseInt(next, 10);
      if (attr == 'related_article' || attr == 'remote_url') next = dc.inflector.normalizeUrl(next);
      if (next != original[attr] && el.hasClass('change')) changes[attr] = next;
    }, this));

      //URL validation
      var errors = _.any(['related_article', 'remote_url'], _.bind(function(attr) {
      if (changes[attr] && !this.validateUrl(changes[attr])) {
        this.$('#document_edit_' + attr).addClass('error');
        return true;
      }
    }, this));

    //Title validation
    if( this.$('#document_edit_title').val() == ""){
        this.$('#document_edit_title').addClass('error');
        this.error(_.t('must_have_title'));
        errors = true;
    }

    if (errors) return false;

      this.close();
    if (!_.isEmpty(changes)) {
      _.each(this.docs, function(doc){ doc.save(changes); });
      if (!_.any(this.docs, function(doc) { return doc.suppressNotifier; })) {
        dc.ui.notifier.show({
          mode : 'info', 
          text : 'Updated ' + this.docs.length + ' ' +
                 dc.inflector.pluralize('document', this.docs.length)
        });
      }
    }
  },

  // Confirms deletion of multiple documents.
  destroy : function() {
    this.close();
    if (Documents.selected().length == 0) {
      this.docs[0].set({'selected': true});
    }
    Documents.verifyDestroy(Documents.selected());
  },

  // Sets the dialog title to include the number of documents or title of
  // the single document being edited.
  _title : function() {
    return (this.multiple) ? 
      _.t('x_documents',this.docs.length) :
      '"' + dc.inflector.truncate(this.docs[0].get('title'), 35) + '"';
  },

  // On change, mark input field as dirty.
  _markChanged : function(e) {
    $(e.target).addClass('change');
  },

  // Find the attributes that are common between all documents. Used to 
  // display those attributes in the fields. Fields that have different
  // values will be blank.
  _sharedAttributes : function() {
    return _.reduce(this.ATTRIBUTES, _.bind(function(memo, attr) {
      memo[attr] = Documents.sharedAttribute(this.docs, attr);
      return memo;
    }, this), {});
  }

}, {
  
  // This static method is used for conveniently opening the dialog for 
  // any selected documents.
  open : function(doc) {
    var docs = Documents.chosen(doc);
    if (!docs.length) return;
    if (!Documents.allowedToEdit(docs)) return;
    new dc.ui.DocumentDialog(docs);
  }

});

// Dialog for embedding a document viewer on a third-party site. Checks the
// access level, presents [saved] preferences, fetches the document embed code.
dc.ui.DocumentEmbedDialog = dc.ui.Dialog.extend({

  events : {
    'click .preview'        : 'preview',
    'change select'         : 'update',
    'click select'          : 'update',
    'keyup input'           : 'update',
    'focus input'           : 'update',
    'click input'           : 'update',
    'change input'          : 'update',
    'change .viewer_open_to': '_renderOpenTo',
    'click .next'           : 'nextStep',
    'click .previous'       : 'previousStep',
    'click .close'          : 'close',
    'click .snippet'        : 'selectSnippet',
    'click .set_publish_at' : 'openPublishAtDialog',
    'click .edit_access'    : 'editAccessLevel',
    'click .remove_lines'   : 'removeLines'
  },

  totalSteps : 3,

  // Off by 1 to maintain sanity. Additionally, the first step is always custom.
  STEPS : [null, null,
           _.t('embed_step_two_title'),
           _.t('embed_step_three_title')
  ],

  DEMO_ERROR : _.t('demo_embed_error', '<a href="/contact">','</a>', '<a href="/help/publishing">','</a>'),

  DEFAULT_OPTIONS : {
    width   : null,
    height  : null,
    sidebar : true,
    text    : true
  },

  // Can't have an embed dialog without a document.
  constructor : function(doc) {
    this.model = doc;
    this.currentStep = 1;
    dc.ui.Dialog.call(this, {mode : 'custom', title : this.displayTitle()});
    this.render();
  },

  // Sets up all jQuery selectors and shows the first step of the embedding process.
  render : function() {
    if (dc.account.organization().get('demo')) return dc.ui.Dialog.alert(this.DEMO_ERROR);
    dc.ui.Dialog.prototype.render.call(this);
    this.$('.custom').html(JST['workspace/document_embed_dialog']({doc: this.model}));
    this._next          = this.$('.next');
    this._previous      = this.$('.previous');
    this._widthEl       = this.$('input[name=width]');
    this._heightEl      = this.$('input[name=height]');
    this._viewerSizeEl  = this.$('select[name=viewer_size]');
    this._sidebarEl     = this.$('input[name=sidebar]');
    this._showTextEl    = this.$('input[name=show_text]');
    this._showPDFEl     = this.$('input[name=show_pdf]');
    this._openToEl      = this.$('.open_to');
    if (dc.app.preferences.get('document_embed_options')) this._loadPreferences();
    this.setMode('document_embed', 'dialog');
    this.update();
    this.setStep();
    this.center();
    return this;
  },

  // The first step is always custom for the document.
  displayTitle : function() {
    if (this.currentStep == 1) return _.t("embed_step_one_title", dc.inflector.truncate(this.model.get('title'), 25) );
    return this.STEPS[this.currentStep];
  },

  // Opens the document embed preview (with options) in a new window using a generated URL.
  preview : function() {
    var options = encodeURIComponent(JSON.stringify(this.embedOptions()));
    var url = '/documents/' + this.model.canonicalId() + '/preview?options=' + options;
    window.open(url);
    return false;
  },

  // Called after every keystroke and input change. Used to save selected options
  // and modify any options that need modifying.
  update : function() {
    this._toggleDimensions();
    this._savePreferences();
  },

  // Returns an object literal with all of the embed options serialized.
  embedOptions : function() {
    var options = {};
    var openToPage = this.$('.page_select').val();
    var openToNote = this.$('.note_select').val();
    if (this._viewerSizeEl.val() == 'fixed') {
      var width   = parseInt(this._widthEl.val(), 10);
      var height  = parseInt(this._heightEl.val(), 10);
      if (width)  options.width  = width;
      if (height) options.height = height;
    }
    if (!this._sidebarEl.is(':checked'))  options.sidebar = false;
    if (!this._showTextEl.is(':checked')) options.text    = false;
    if (!this._showPDFEl.is(':checked'))  options.pdf     = false;
    if (openToPage) options.page = parseInt(openToPage, 10);
    if (openToNote) {
      var note = this.model.notes.get(parseInt(openToNote, 10));
      options.page = note.get('page');
      options.note = note.id;
    }
    return options;
  },

  // If necessary, let the user change the document's access level before embedding.
  editAccessLevel : function() {
    this.close();
    Documents.editAccess([this.model]);
  },

  // Optionally, set a document `publish_at` time by opening a new dialog.
  openPublishAtDialog : function() {
    this.close();
    new dc.ui.PublicationDateDialog([this.model]);
  },

  // Remove line breaks from the viewer embed.
  removeLines : function() {
    this.$('.snippet').val(this.$('.snippet').val().replace(/[\r\n]/g, ''));
  },

  // Serialize and stringify embed options so they remain consistent between embeds.
  _savePreferences : function() {
    dc.app.preferences.set({document_embed_options : JSON.stringify(this.embedOptions())});
  },

  // Read serialized embed options from user's cookie so embed options remain consistent
  // between embeds. *Nifty.*
  _loadPreferences : function() {
    var options = JSON.parse(dc.app.preferences.get('document_embed_options')) || this.DEFAULT_OPTIONS;
    if (options.width || options.height) this._viewerSizeEl.val('fixed');
    this._widthEl.val(options.width);
    this._heightEl.val(options.height);
    this._showPDFEl.attr('checked', options.pdf );
    this._sidebarEl.attr('checked', options.sidebar);
    this._showTextEl.attr('checked', options.text);
  },

  // Handles user selection of dropdown that controls which page/annotation
  // the viewer opens on. Changes form, which is later serialized.
  _renderOpenTo : function(e) {
    switch ($(e.currentTarget).val()) {
      case 'first_page':
        return this._openToEl.empty();
      case 'page':
        return this._openToEl.html(JST['document/page_select']({doc : this.model}));
      case 'note':
        this.model.ignoreNotes = true;
        this.model.notes.fetch({success : _.bind(function() {
          this._openToEl.html(JST['document/note_select']({doc : this.model}));
          delete this.model.ignoreNotes;
        }, this)});
    }
  },

  // Collects embed options and renders them using a JST template in a textarea.
  _renderEmbedCode : function() {
    var options       = this.embedOptions();
    options.container = '"#DV-viewer-' + this.model.canonicalId() + '"';
    var serialized    = _.map(options, function(value, key){ return key + ': ' + value; });
    this.$('.publish_embed_code').html(JST['document/embed_code']({
      doc: this.model,
      options: serialized.join(',\n    '),
      rawOptions: options
    }));
  },

  // After every keystroke or input change, check if the viewer size has been set.
  _toggleDimensions : function() {
    this.$('.dimensions').toggle(this._viewerSizeEl.val() == 'fixed');
  },

  // On step 1, if a user changes any document attributes, save them and only
  // continue to step 2 if there are no model errors.
  saveUpdatedAttributes : function() {
    var access = this.$('input[name=access_level]').is(':checked') ?
                 dc.access.PUBLIC : this.model.get('access');
    var relatedArticle = this.$('input[name=related_article]').removeClass('error').val();
    var attrs = {
      access          : access,
      related_article : dc.inflector.normalizeUrl(relatedArticle)
    };
    if (attrs = this.model.changedAttributes(attrs)) {
      var errors = _.any(['related_article'], _.bind(function(attr) {
        if (attrs[attr] && !this.validateUrl(attrs[attr])) {
          this.$('input[name=' + attr + ']').addClass('error');
          return true;
        }
      }, this));
      if (errors) return false;
      dc.ui.spinner.show();
      this.model.save(attrs, {success : function(){ dc.ui.spinner.hide(); }});
    }
    return true;
  },

  // Advances to the next step, checking if any errors are returned beforehand from
  // steps that have server-side side-effects.
  nextStep : function() {
    if (this.currentStep == 1 && !this.saveUpdatedAttributes()) return false;
    if (this.currentStep >= this.totalSteps) return this.close();
    if (this.currentStep == 2) this._renderEmbedCode();
    this.currentStep += 1;
    this.setStep();
  },

  // Simply goes backwards.
  previousStep : function() {
    if (this.currentStep > 1) this.currentStep -= 1;
    this.setStep();
  },

  // Regardless of direction, switches the template to only show the active step.
  // Also updates buttons, titles, and step information.
  setStep : function() {
    this.title(this.displayTitle());

    this.$('.publish_step').setMode('not', 'enabled');
    this.$('.publish_step_'+this.currentStep).setMode('is', 'enabled');
    this.info( _.t('step_x_of_x', this.currentStep, this.totalSteps), true);

    var first = this.currentStep == 1;
    var last = this.currentStep == this.totalSteps;

    this._previous.setMode(first ? 'not' : 'is', 'enabled');
    this._next.html(last ? _.t('finish') : _.t('next')+' &raquo;').setMode('is', 'enabled');
  },

  // Auto-selects the embed code when user clicks on the textarea.
  selectSnippet : function() {
    this.$('.snippet').select();
  }

});

// Responsible for rendering a list of Documents. The tiles can be displayed
// in a number of different sizes.
dc.ui.DocumentList = Backbone.View.extend({

  // Status text
  DOC_STATUS : [
    'New',
    'In Data Entry (1 Claim)',
    'In Data Entry (2 Claims)',
    'Ready for Quality Control',
    'In Quality Control',
    'Ready for Quality Assurance',
    'In Quality Assurance',
    'Ready for Extraction',
    'Ready for Supplemental Data Entry',
    'In Supplemental Data Entry',
    'Ready for Supplemental Quality Control',
    'In Supplemental Quality Control',
    'Ready for Supplemental Quality Assurance',
    'In Supplemental Quality Assurance'
  ],

  // Threshold for dragging empty space in the document list. When below
  // this threshold, do not deselect documents.
  SLOP      : 3,

  id        : 'document_list',

  events : {
    'mousedown': '_startDeselect',
    'click':     '_endDeselect'
  },

  // Listen for new documents on the `Documents` Collection, which is populated
  // at load time and when searching.
  initialize: function(options) {
    _.bindAll(this, 'reset', '_removeDocument', '_addDocument', '_onSelect',
              '_maybeSelectOrDelete');
    $(document).bind('keydown', this._maybeSelectOrDelete);
    Documents.bind('reset',   this.reset);
    Documents.bind('remove',  this._removeDocument);
    Documents.bind('add',     this._addDocument);
  },

  // Simply renders the list container, still waiting for documents.
  render : function() {
    $('.search_tab_content').selectable({
      ignore : '.noselect, .minibutton',
      select : '.icon.doc',
      onSelect : this._onSelect
    });
    return this;
  },

  // Once there are Documents being populated, this view listens for a `Collection#reset`,
  // which redraws all the Document tile views in this list view.
  reset : function() {
    $(window).unbind('resize.entities');
    this.$el.html('');
    var views = Documents.map(function(m){
      return (new dc.ui.Document({model : m})).render({notes: true}).el;
    });
    this.$el.append(views.concat(this.make('div', {'class' : 'clear'})));
  },

  // When document tiles are lassoed by `.selection` in `render`, set their
  // `selected` attribute. Allows multiple document selection.
  _onSelect : function(els) {
    var active = {};
    _.each(els, function(icon) {
      var id = $(icon).attr('data-id');
      active[id] = true;
      Documents.get(id).set({selected : true});
    });
    if (!dc.app.hotkeys.shift && !dc.app.hotkeys.command) {
      _.each(Documents.selected(), function(doc) {
        if (!active[doc.id]) doc.set({selected : false});
      });
    }
  },

  // Handle cmd-a for select all.
  _maybeSelectOrDelete : function(e) {
    var del  = Documents.selectedCount && (e.which == 8);
    var cmdA = dc.app.hotkeys.command && (e.which == 97 || e.which == 65);
    if (!(cmdA || del) || $(e.target).closest('input, textarea').length) return;
    if (cmdA) {
      Documents.selectAll();
    } else if (del) {
      Documents.verifyDestroy(Documents.selected());
    }
    return false;
  },

  // Called when clicking on the document list but not on a document tile.
  // Used to check if the user is dragging a lasso around documents.
  _startDeselect : function(e) {
    this._pageX = e.pageX;
    this._pageY = e.pageY;
  },

  // On mouseup, check if the user is dragging a selection lasso. Has a grace
  // (`this.SLOP`) of a few pixels where a drag can still be considered a
  // deselect.
  _endDeselect : function(e) {
    if (dc.app.hotkeys.shift ||
        dc.app.hotkeys.command ||
        dc.app.hotkeys.control) return;
    if ($(e.target).hasClass('doc_title') ||
        $(e.target).hasClass('doc') ||
        $(e.target).hasClass('edit_glyph')) return;
    if ((Math.abs(e.pageX - this._pageX) > this.SLOP) ||
        (Math.abs(e.pageY - this._pageY) > this.SLOP)) return;

    Documents.deselectAll();
  },

  // Called when document models added to `Documents` collection.
  _addDocument : function(doc) {
    var view = new dc.ui.Document({model : doc});
    this.$el.prepend(view.render().el);
  },

  // Called when document models deleted (and subsequently removed from `Documents`
  // collection).
  _removeDocument : function(doc) {
    $('#document_' + doc.id).remove();
  }

});
// A tile view for a document's annotations, listed per-document. Allows
// note editing by those who have permission.
dc.ui.Note = Backbone.View.extend({

  className : 'note noselect',

  events : {
    'click .title_link':       'viewNoteInDocument',
    'click .edit_note':        'editNote',
    'click .cancel_note':      'cancelNote',
    'click .save_note':        'saveNote',
    'click .save_draft_note':  'saveNote',
    'click .delete_note':      'deleteNote'
  },

  // Re-render the note when saved.
  initialize : function(options) {
    this.options = options;
    _.bindAll(this, 'render');
    this.model.bind('change', this.render);
  },

  // Renders the note, adding classes that show editing controls if allowed.
  render : function() {
    var data = _.extend(this.model.toJSON(), {
      note          : this.model,
      ownsNote      : true,
      disableLinks  : this.options.disableLinks || false
    });
    $(this.el).html(JST['document/note'](data));
    this.setMode('display', 'visible');
    this.setMode(this.model.get('access'), 'access');
    this.setMode(this.model.checkAllowedToEdit() ? 'is' : 'not', 'editable');
    return this;
  },

  // If the viewport is narrower than the note, center the note.
  center : function() {
    var $excerpt       = this.$('.note_excerpt');
    var coords         = this.model.coordinates();
    if (!coords) return;
    var annoCenter     = coords.left + (coords.width / 2);
    var viewportWidth  = $excerpt.closest('.note_excerpt_wrap').width();
    var viewportCenter = viewportWidth / 2;

    if (coords.left + coords.width > viewportWidth) {
      if (coords.width > viewportWidth) {
        $excerpt.css('left', -1 * coords.left);
      } else {
        $excerpt.css('left', viewportCenter - annoCenter);
      }
    }
  },

  // Opens the note in a document in a new window.
  viewNoteInDocument : function() {
    var suffix = '#document/p' + this.model.get('page') + '/a' + this.model.get('id');
    window.open(this.model.document().viewerUrl() + suffix);
  },

  // Checks permissions and turns on note editing.
  editNote : function() {
    if (!this.model.checkAllowedToEdit()) {
      return dc.ui.Dialog.alert("You don't have permission to edit this note.");
    }
    this.$('.note_title_input').val(this.model.get('title'));
    this.$('.note_text_edit').val(this.model.get('content'));
    this.setMode('edit', 'visible');
  },

  // Simply turns off note editing.
  cancelNote : function() {
    this.setMode('display', 'visible');
  },

  // Sends a server request by saving the note model on the document. Also sets up
  // the correct access level based on which submit button was pressed.
  // The user cna only edit existing notes, not save new notes, hence no
  // need to update note counts on the parent document.
  saveNote : function(e) {
    var access = this.model.get('access');
    if ($(e.target).hasClass('save_draft_note')) {
      access = 'exclusive';
    } else if (this.model.get('access') == 'exclusive') {
      access = 'public';
    }
    this.model.save({
      title   : this.$('.note_title_input').val(),
      content : this.$('.note_text_edit').val(),
      access  : access
    });
    this.render();
  },

  // Sends a server request destroying the note model on the document. Also updates
  // the document model with [assumed] correct number of notes.
  deleteNote : function() {
    dc.ui.Dialog.confirm('Are you sure you want to delete this note?', _.bind(function() {
      this.model.destroy({success : _.bind(function() {
        $(this.el).remove();
        this.model.document().decrementNotes();
      }, this)});
      return true;
    }, this));
  }

});

dc.ui.NoteEmbedDialog = dc.ui.Dialog.extend({

  events : {
    'change select'         : 'update',
    'click select'          : 'update',
    'click .next'           : 'nextStep',
    'click .previous'       : 'previousStep',
    'click .close'          : 'close',
    'click .snippet'        : 'selectSnippet',
    'click .set_publish_at' : 'openPublishAtDialog',
    'click .edit_access'    : 'editAccessLevel',
    'click .remove_lines'   : 'removeLines'
  },

  totalSteps : 2,

  STEPS : [null,
    _.t('embed_note_step_one'),
    _.t('embed_note_step_two')
  ],

  DEMO_ERROR : _.t('embed_note_demo_error','<a href="/contact">','</a>','<a href="/help/publishing#step_5">','</a>'),

  DEFAULT_OPTIONS : {},

  constructor : function(doc, initialNoteId) {
    this.currentStep   = 1;
    this.doc           = doc;
    this.initialNoteId = initialNoteId;
    this.height        = 0;
    dc.ui.Dialog.call(this, {mode : 'custom', title : this.displayTitle()});
    dc.ui.spinner.show();
    this.fetchNotes();
  },

  fetchNotes : function() {
    this.doc.notes.fetch({
      silent  : true,
      success : _.bind(function() {
        dc.ui.spinner.hide();
        this.render();
      }, this)
    });
  },

  render : function() {
    if (dc.account.organization().get('demo')) return dc.ui.Dialog.alert(this.DEMO_ERROR);
    dc.ui.Dialog.prototype.render.call(this);
    this.$('.custom').html(JST['workspace/note_embed_dialog']({
      doc           : this.doc,
      notes         : this.doc.notes.select(function(note) { return note.get('access') == 'public'; }),
      initialNoteId : this.initialNoteId
    }));
    this._next          = this.$('.next');
    this._previous      = this.$('.previous');
    this._noteSelectEl  = this.$('select[name=note]');
    this._preview       = this.$('.note_preview');
    this.setMode('embed', 'dialog');
    this.setMode('note_embed', 'dialog');
    this.update();
    this.setStep();
    this.center();
    dc.ui.spinner.hide();
    return this;
  },

  displayTitle : function() {
    return this.STEPS[this.currentStep];
  },

  update : function() {
    var id = parseInt(this._noteSelectEl.val(), 10);
    this.note = this.doc.notes.get(id);
    this._renderNote();
    this._renderEmbedCode();
    if (this._preview.height() > this.height) {
      this.center();
      this.height = this._preview.height();
    }
  },

  // Remove line breaks from the viewer embed.
  removeLines : function() {
    this.$('.snippet').val(this.$('.snippet').val().replace(/[\r\n]/g, ''));
  },

  _renderNote : function() {
    var noteView = new dc.ui.Note({
      model         : this.note,
      collection    : this.doc.notes,
      disableLinks  : true
    });
    this.$('.note_preview').html(noteView.render().el);
    noteView.center();
  },

  embedOptions : function() {
    var options = {};
    return options;
  },

  _renderEmbedCode : function() {
    var options          = this.embedOptions();
    var serialized       = _.map(options, function(value, key){ return key + ': ' + value; });
    this.$('.publish_embed_code').html(JST['workspace/note_embed_code']({
      note    : this.note,
      options : serialized.join(',\n    ')
    }));
  },

  nextStep : function() {
    this.currentStep += 1;
    if (this.currentStep > this.totalSteps) return this.close();
    if (this.currentStep == 2) this.update();
    this.setStep();
  },

  previousStep : function() {
    if (this.currentStep > 1) this.currentStep -= 1;
    this.setStep();
  },

  setStep : function() {
    this.title(this.displayTitle());

    this.$('.publish_step').setMode('not', 'enabled');
    this.$('.publish_step_'+this.currentStep).setMode('is', 'enabled');
    this.info( _.t('step_x_of_x', this.currentStep, this.totalSteps), true);

    var first = this.currentStep == 1;
    var last = this.currentStep == this.totalSteps;

    this._previous.setMode(first ? 'not' : 'is', 'enabled');
    this._next.html(last ? _.t('finish') : _.t('next') + ' &raquo;').setMode('is', 'enabled');
  },

  selectSnippet : function() {
    this.$('.snippet').select();
  },

  // If necessary, let the user change the document's access level before embedding.
  editAccessLevel : function() {
    this.close();
    Documents.editAccess([this.doc]);
  },

  // Optionally, set a document `publish_at` time by opening a new dialog.
  openPublishAtDialog : function() {
    this.close();
    new dc.ui.PublicationDateDialog([this.doc]);
  }

});

// Simple dialog used to set a publication date for multiple documents. This
// changes a document's access level to public (from private or organization).
dc.ui.PublicationDateDialog = dc.ui.Dialog.extend({

  id        : 'pubdate_dialog',
  className : 'dialog',

  events : {
    'click .cancel'     : 'close',
    'click .ok'         : 'save',
    'click .delete'     : 'removeDate',
    'click .public_now' : 'editAccess'
  },

  // Takes an array of Document models. Checks permissions and renders the dialog.
  constructor : function(docs) {
    if (!Documents.allowedToEdit(docs)) return;
    this.docs = docs;
    this.multiple = docs.length > 1;
    var title = _.t('set_publication_date_for', this._title() );
    dc.ui.Dialog.call(this, {
      mode        : 'custom',
      title       : title,
      editor      : true,
      closeText   : _.t('cancel'),
      deleteText  : _.t('remove')
    });
    this.render();
    $(document.body).append(this.el);
  },

  // Builds the dialog template using the initialized array of documents.
  render : function() {
    dc.ui.Dialog.prototype.render.call(this);
    this._container = this.$('.custom');
    var publishAt = Documents.sharedAttribute(this.docs, 'publish_at');
    var oneHour = 60 * 60 * 1000;
    this._container.html(JST['document/publication_date_dialog']({
      documentCount: this.docs.length,
      date:     publishAt ? 
                DateUtils.parseRfc(publishAt) : 
                new Date(+(new Date) + oneHour)
    }));
    this.center();
    return this;
  },

  // If the user opts to change access levels immediately, close this dialog
  // and open up the `editAccess` dialog that's constructed in the `Documents`
  // collection.
  editAccess : function() {
    this.close();
    Documents.editAccess(this.docs);
  },

  // Validation and saving `publish_at` field to each `Document` model.
  save : function() {
    var date = this._getDate();
    if (date < new Date) {
      this.close();
      dc.ui.Dialog.alert(_.t('no_past_publication') );
      return;
    }
    var utc = JSON.stringify(date);
    _.each(this.docs, function(doc){ doc.save({publish_at : utc}); });
    this.close();
  },

  // Unset `publish_at`.
  removeDate : function() {
    _.each(this.docs, function(doc){ doc.save({publish_at : null}); });
    this.close();
  },

  // Helper method that constructs a title for multiple documents.
  _title : function() {
    if (this.multiple) return _.t('x_documents', this.docs.length );
    return '"' + dc.inflector.truncate(this.docs[0].get('title'), 35) + '"';
  },

  // Helper method that reads the date form fields (month, day, year, time)
  // and constructs a JavaScript `Date` object.
  _getDate : function() {
    return new Date(
      this.$('.date_year').val(),
      parseInt(this.$('.date_month').val(), 10) - 1,
      this.$('.date_day').val(),
      this.$('.date_hour').val()
    );
  }

});

dc.ui.SparkEntities = Backbone.View.extend({

  BLOCK_WIDTH: 275,

  LEFT_WIDTH: 120,

  RIGHT_MARGIN: 50,

  MAX_WIDTH: 700,

  TOOLTIP_DELAY: 200,

  RESIZE_DELAY: 333,

  SEARCH_DISTANCE: 5,

  events: {
    'click .cancel_search'           : 'hide',
    'click .show_all'                : 'renderKind',
    'click .entity_line_title'       : '_showPages',
    'click .entity_list_title'       : '_showPages',
    'click .entity_bucket_wrap'      : '_openEntity',
    'click .arrow.left'              : 'render',
    'mouseleave .entity_buckets'     : 'hideTooltip',
    'mouseenter .entity_bucket_wrap' : '_onMouseEnter',
    'mouseleave .entity_bucket_wrap' : '_onMouseLeave'
  },

  initialize : function(options) {
    this.options || (this.options = options);
    this.template = JST['document/entities'];
    this.options.container.append(this.el);
    this.showLater = _.debounce(this.showLater, this.TOOLTIP_DELAY);
    this.rerenderLater = _.debounce(_.bind(this.rerender, this), this.RESIZE_DELAY);
    this._renderState = {doc: this.model, distance: this.SEARCH_DISTANCE};
  },

  show : function() {
    this._open = true;
    $(window).bind('resize.entities', this.rerenderLater);
    this.render();
  },

  render : function() {
    this._renderState.only = false;
    this.rerender();
    return this;
  },

  renderKind : function(e) {
    this._renderState.only = $(e.currentTarget).attr('data-kind');
    this.rerender();
    this.model.trigger('focus');
  },

  calculateWidth : function() {
    var elWidth = $(this.el).width();
    if (this._renderState.only) {
      return Math.min(elWidth - (this.LEFT_WIDTH + this.RIGHT_MARGIN), this.MAX_WIDTH);
    } else {
      var rows = Math.floor(elWidth / (this.BLOCK_WIDTH + this.LEFT_WIDTH + this.RIGHT_MARGIN));
      return Math.min(Math.floor((elWidth - ((this.LEFT_WIDTH + this.RIGHT_MARGIN) * rows)) / rows), this.MAX_WIDTH);
    }
  },

  rerender : function() {
    this.options.container.show();
    var options = _.extend(this._renderState, {width: this.calculateWidth()});
    $(this.el).html(this.template(options));
  },

  hide : function() {
    this._open = false;
    $(window).unbind('resize.entities', this.rerenderLater);
    $(this.el).html('');
    this.options.container.hide();
  },

  showTooltip : function() {
    if (!this._current) return;
    var excerpt;
    var next = _.bind(function(excerpt) {
      if (this._current) {
        dc.ui.tooltip.show({
          left      : this._current.offset().left,
          top       : this._current.offset().top + 15,
          title     : this._entity.get('value'),
          text      : '<b>p.' + excerpt.page_number + '</b> ' + dc.inflector.trimExcerpt(excerpt.excerpt),
          leaveOpen : true
        });
      }
    }, this);
    if (excerpt = this._entity.excerpts[this._occurrence]) {
      next(excerpt);
    } else {
      this.showLater(next);
    }
  },

  showLater : function(next) {
    if (this._current) this._entity.loadExcerpt(this._occurrence, next);
  },

  hideTooltip : function() {
    dc.ui.tooltip.hide();
  },

  _setCurrent : function(e) {
    var found;
    var occ = 'data-occurrence';
    var el = $(e.currentTarget);
    if (el.hasClass('occurs')) {
      var found = el;
    } else {
      var index = el.index();
      var buckets = el.parent().children();
      for (var i = 1, l = this.SEARCH_DISTANCE; i <= l; i++) {
        var after = buckets[index + i], before = buckets[index - i];
        if (el = ($(after).hasClass('occurs') && $(after)) ||
                 ($(before).hasClass('occurs') && $(before))) break;
      }
    }
    if (!el) {
      this.hideTooltip();
      return false;
    }
    el.addClass('active');
    this._current = el;
    this._occurrence = el.find('.entity_bucket').attr(occ);
    var id = el.closest('.entity_line').attr('data-id');
    this._entity = this.model.entities.get(id);
    return true;
  },

  _openEntity : function(e) {
    if (!this._setCurrent(e)) return;
    this.model.openEntity(this._entity.id, this._occurrence.split(':')[0]);
  },

  _onMouseEnter : function(e) {
    if (!this._setCurrent(e)) return;
    this.showTooltip();
  },

  _onMouseLeave : function() {
    if (this._current) this._current.removeClass('active');
    this._occurrence = this._entity = this._current = null;
  },

  _showPages : function(e) {
    var id = $(e.currentTarget).closest('[data-id]').attr('data-id');
    dc.model.Entity.fetchId(this.model.id, id, _.bind(function(entities) {
      this.hide();
      this.model.pageEntities.reset(entities);
    }, this));
  }

});

// The UploadDialog handles bulk upload via the HTML5 jQuery file upload plugin.
dc.ui.UploadDialog = dc.ui.Dialog.extend({

  id        : 'upload_dialog',
  className : 'dialog',

  INSERT_PAGES_MESSAGE: _.t('insert_pages_message'),

  // Sets up the uploader only when the Documents tab is opened. Manually use `setupUpload`.
  constructor : function(options) {
    var defaults = {
      editable        : true,
      insertPages     : false,
      autostart       : false,
      collection      : UploadDocuments,
      mode            : 'custom',
      title           : _.t('upload_document'),
      saveText        : _.t('upload'),
      closeText       : _.t('cancel'),
      multiFileUpload : false
    };
    options = _.extend({}, defaults, options);

    _.bindAll(this, 'setupUpload', '_countDocuments', 'cancelUpload',
      '_onSelect', '_onProgress', '_onComplete', '_onAllComplete');
    dc.ui.Dialog.call(this, options);
    if (options.autostart) $(this.el).addClass('autostart');
    if (dc.app.navigation) {
      dc.app.navigation.bind('tab:documents', _.bind(function(){ _.defer(this.setupUpload); }, this));
    }
    this.collection.bind('add',   this._countDocuments);
    this.collection.bind('remove', this._countDocuments);
  },

  // Documents are already selected and are being drawn in the dialog.
  render : function() {
    this.options.multiFileUpload = window.FileList &&
                                   ($("input[type=file]")[0].files instanceof FileList);
    this._tiles = {};
    this._project = _.first(Projects.selected());
    var data = {};
    if (this._project) {
      var title = dc.inflector.truncate(this._project.get('title'), 35);
      data.information = 'Project: ' + title;
    }
    dc.ui.Dialog.prototype.render.call(this, data);
    this.$('.custom').html(JST['document/upload_dialog']());
    this._list = this.$('.upload_list');
    this._renderDocumentTiles();
    this._countDocuments();
    this.center();
    if (!this.options.autostart) this.checkQueueLength();
    return this;
  },

  // Each document being uploaded is drawn in a single tile, appended to the dialog's list.
  _renderDocumentTiles : function() {
    var tiles           = this._tiles;
    var editable        = this.options.editable;
    var multiFileUpload = this.options.multiFileUpload;

    this.collection.each(function(model) {
      var view = new dc.ui.UploadDocumentTile({
        editable        : editable,
        model           : model,
        multiFileUpload : multiFileUpload
      });
      tiles[model.id] = view.render();
    });

    var viewEls = _.pluck(_.values(tiles), 'el');
    this._list.append(viewEls);
  },

  // Be careful to only setup file uploading once, when the "Documents" tab is open.
  setupUpload : function() {
    if (this.form || (dc.app.navigation && !dc.app.navigation.isOpen('documents'))) return;
    var uploadUrl = '/import/upload_document';
    if (this.options.insertPages) {
      uploadUrl = '/documents/' + this.options.documentId + '/upload_insert_document';
    }
    this.form = $('#new_document_form');
    this.form.fileUpload({
        url             : uploadUrl,
        onAbort         : this.cancelUpload,
        initUpload      : this._onSelect,
        onProgress      : this._onProgress,
        onLoad          : this._onComplete,
        dragDropSupport : true,
        dropZone        : $('body')
    });
  },

  // If flash is disabled, we fall back to a regular invisible file input field.
  setupFileInput : function() {
    var input = $('#new_document_input');
    input.show().change(_.bind(function() {
      this._project = _.first(Projects.selected());
      $('#new_document_project').val(this._project ? this._project.id : '');
      $('#new_document_form').submit();
    }, this));
  },

  // Initial load of a document, before uploading is begun.
  _onSelect : function(e, files, index, xhr, handler, callback) {
    var file = files[index];
    this.collection.add(new dc.model.UploadDocument({
      id          : dc.inflector.sluggify(file.fileName || file.name),
      uploadIndex : index,
      file        : file,
      position    : index,
      handler     : handler,
      xhr         : xhr,
      startUpload : callback
    }));

    if (index != files.length - 1) return;

    if (this.collection.any(function(file){ return file.overSizeLimit(); })) {
      this.close();
      return dc.ui.Dialog.alert(_.t('max_upload_size_warn','<a href="/help/troubleshooting">',"</a>") );
    }

    this.render();
    if (this.options.autostart) this.startUpload(index);
  },

  // Cancel an upload by index.
  cancelUpload : function(uploadIndex) {
    if (this.collection.length <= 1) {
      this.error( _.t('must_upload_something') );
      return false;
    }
    return true;
  },

  // Called immediately before file to POSTed to server.
  _uploadData : function(id, index) {
    var attrs   = this._tiles[id].serialize();
    var model   = this.collection.get(id);
    var options = this.options;

    model.set(attrs);
    if (options.multiFileUpload && model.get('size')) attrs.multi_file_upload = true;
    attrs.authenticity_token = $("meta[name='csrf-token']").attr("content");
    if (this.$('#make_public').is(':checked')) attrs.make_public = true;
    attrs.email_me = this.$('#email_on_complete').is(':checked') ? this.collection.length : 0;
    if (this._project) attrs.project = this._project.id;
    if (_.isNumber(options.insertPageAt)) attrs.insert_page_at = options.insertPageAt;
    if (_.isNumber(options.replacePagesStart)) attrs.replace_pages_start = options.replacePagesStart;
    if (_.isNumber(options.replacePagesEnd)) attrs.replace_pages_end = options.replacePagesEnd;
    if (options.documentId)  attrs.document_id     = options.documentId;
    if (options.insertPages) attrs.document_number = index + 1;
    if (options.insertPages) attrs.document_count  = this.collection.length;
    if (!options.autostart) this.showSpinner();
    this._list[0].scrollTop = 0;
    return attrs;
  },

  // Update the progress bar based on the browser's determination of upload progress.
  _onProgress : function(e, files, index, xhr, handler) {
    var id         = dc.inflector.sluggify(files[index].fileName || files[index].name);
    var percentage = parseInt((e.loaded / e.total) * 100, 10);

    this._tiles[id].setProgress(percentage);
  },

  // Once done, hide the uploaded document tile and check to see if it's last, so the
  // dialog can close.
  _onComplete : function(e, files, index, xhr, handler) {
    var id   = dc.inflector.sluggify(files[index].fileName || files[index].name);
    var resp = xhr.responseText && JSON.parse(xhr.responseText);

    this._tiles[id].setProgress(100);

    if (resp && resp.bad_request) {
      return this.error("Upload failed.");
    } else if (!this.options.insertPages && resp) {
      Documents.add(new dc.model.Document(resp));
      if (this._project) Projects.incrementCountById(this._project.id);
    } else if (this.options.insertPages && resp) {
      this.documentResponse = resp;
    }

    this._tiles[id].hide();

    if (index == this.collection.length-1) {
      this._onAllComplete();
    } else {
      this.startUpload(index+1);
    }
  },

  // Once there are no uploads left, either close the dialog, or alert the user that everything
  // is completed.
  _onAllComplete : function() {
    this.hideSpinner();
    if (this.options.insertPages) {
      try {
        window.opener && window.opener.Documents &&
          window.opener.Documents.get(this.options.documentId).set(this.documentResponse);
      } catch (e) {
        // No parent window...
      }
      dc.ui.Dialog.alert(this.INSERT_PAGES_MESSAGE, {onClose : function() {
        window.close();
        _.defer(dc.ui.Dialog.alert, _.t('close_while_text_reprocess') );
      }});
    }
    this.close();
  },

  // Update title and fields to match the new count of uploads.
  _countDocuments : function() {
    var num = this.collection.length;
    
    this.title( _.t('uploaded_x_documents',num) );

    this.$('.upload_public_count').text( _.t('x_documents', num ) );
    this.$('.upload_email_count').text( _.t('uploaded_x_document_has', num ) );
  },

  // Ask the server how many work units are currently queued.
  checkQueueLength : function() {
    $.getJSON('/documents/queue_length.json', {}, _.bind(function(resp) {
      var num = resp.queue_length;
      if (num <= 0) return;
      this.info( _.t('document_processing_count', num ), true );
    }, this));
  },

  // Used by the ReplacePagesEditor to include the `insertPagesAt`, `replacePagesStart`,
  // and `replacePagesEnd` options.
  insertPagesAttrs : function(attrs) {
    _.each(attrs, _.bind(function(value, attr) {
      this.options[attr] = value;
    }, this));
  },

  // Overriding `Dialog#confirm` to validate titles.
  confirm : function() {
    var failed = _.select(this._tiles, function(tile) { return tile.ensureTitle(); });
    if (failed.length) {
      var num = this.collection.length;
      return this.error( _.t('must_have_doc_title', num ) );
    }
    this.$('.ok').setMode('not', 'enabled');
    this.startUpload(0);
  },

  // Starts the first/next upload. Used by autostart and the Submit button.
  startUpload : function(index) {
    var tiles = this._tiles;
    var doc = this.collection.models[index];
    doc.get('handler').formData = this._uploadData(doc.get('id'), index);
    doc.get('startUpload')();
    tiles[doc.get('id')].startProgress();
  },

  // User-initiated close
  cancel : function() {
    this.close();
  },

  // Closes dialog when finished. Clears out collection for next upload.
  close : function() {
    this.collection.reset();
    dc.ui.Dialog.prototype.close.call(this);
  }

});

// Each upload tile is used to represent a single document waiting to be uploaded.
dc.ui.UploadDocumentTile = Backbone.View.extend({

  className : 'row',

  events : {
    'click .remove_queue' : 'removeUploadFile',
    'click .open_edit'    : 'openEdit',
    'click .apply_all'    : 'applyAll'
  },
  
  initialize: function(options) { this.options = options; },

  // Renders tile and sets up commonly used jQuery selectors.
  render : function() {
    var template = JST['document/upload_document_tile']({
      editable        : this.options.editable,
      autostart       : this.options.autostart,
      model           : this.model,
      multiFileUpload : this.options.multiFileUpload
    });
    $(this.el).html(template);
    this._title    = this.$('input[name=title]');
    this._progress = this.$('.progress_bar');
    return this;
  },

  // Serialize user-submitted form data for the document's various attributes.
  serialize : function() {
    return {
      title             : this._title.val(),
      study             : this.$('input[name=study]').val(),
      description       : this.$('textarea[name=description]').val(),
      source            : this.$('input[name=source]').val(),
      related_article   : this.$('input[name=source_ref]').val(),
      language          : this.$('select[name=language]').val()
    };
  },

  // Clears out upload tile and also removes model from Upload Dialog's collection.
  removeUploadFile : function() {
    if (dc.app.uploader.cancelUpload(this.model.get('uploadIndex'))) {
      this.hide();
      this.model.get('xhr').abort();
      UploadDocuments.remove(this.model);
    }
  },

  // Apply the current file's attributes to all files in the upload.
  applyAll : function() {
    var dialog = dc.app.uploader.el;
    var attrs  = this.serialize();
    $('input[name=study]',          dialog).val(attrs.study);
    $('input[name=source]',         dialog).val(attrs.source);
    $('input[name=source_ref]',     dialog).val(attrs.related_article);
    $('textarea[name=description]', dialog).val(attrs.description);
    $('select[name=language]',      dialog).val(attrs.language);
    dc.app.uploader.info( _.t('update_applied_all') );
  },

  // Toggle used to show/hide upload document's user-editable attributes.
  openEdit : function() {
    this.$('.upload_edit').toggle();
    this.$('.open_edit').toggleClass('active');
  },

  // Called by uploader, used to zero-out progress bar. IE doesn't have a progress bar.
  startProgress : function() {
    this._percentage = 0;
    if (this.options.multiFileUpload) {
      this._progress.show();
    }
  },

  // Smoothly animate progress bar to browser-supplied level.
  setProgress : function(percentage) {
    if (percentage <= this._percentage) return;
    this._percentage = percentage;
    this._progress.stop(true).animate({width: percentage + '%'}, {queue: false, duration: 400});
  },

  // Validation used by uploader dialog.
  ensureTitle : function() {
    var noTitle = dc.inflector.trim(this._title.val()) == '';
    this._title.closest('.text_input').toggleClass('error', noTitle);
    return noTitle;
  },

  // Hide document tile when finished uploading.
  hide : function() {
    $(this.el).animate({opacity: 0}, 200).slideUp(200, function(){ $(this).remove(); });
  }

});

dc.ui.EntityList = Backbone.View.extend({

  id : 'entities',

  events : {
    'click .row'           : '_filterFacet',
    'click .cancel_search' : '_removeFacet',
    'click .more'          : '_loadFacet',
    'click .less'          : '_showLess',
    'click .show_pages'    : '_showPages'
  },

  ENTITY_KINDS : ['city', 'country', 'term', 'state', 'person', 'place', 'organization', 'email', 'phone'],

  // Refresh the facets with a new batch.
  renderFacets : function(facets, limit, docCount) {
    this._docCount = docCount;
    var filtered   = this.extractEntities(dc.app.searchBox.value());
    _.each(filtered, function(filter) {
      var list  = facets[filter.type];
      if (!list) return;
      var index = null;
      var facet = _.detect(list, function(f, i) {
        index = i;
        return f.value.toLowerCase() == filter.value.toLowerCase();
      });
      if (facet) {
        facet.active = true;
        list.splice(index, 1);
      } else {
        facet = {value : filter.value, count : docCount, active : true};
        list.pop();
      }
      facets[filter.type].unshift(facet);
    });
    this._facets = facets;
    $(this.el).html(JST['workspace/entities']({entities : facets, limit : limit}));
    dc.app.scroller.checkLater();
  },

  // Just add to the facets, don't blow them away.
  mergeFacets : function(facets, limit, docCount) {
    this.renderFacets(_.extend(this._facets, facets), limit, docCount);
  },

  _facetValueFor : function(el) {
    var row = $(el).closest('.row');
    var val = row.attr('data-value');
    var cat = row.attr('data-category');
    return {category: cat, value: val};
  },

  _filterFacet : function(e) {
    var facet = this._facetValueFor(e.target);
    dc.app.searcher.addToSearch(facet.category, facet.value);
  },

  _removeFacet : function(e) {
    $(e.target).closest('.row').removeClass('active');
    var facet = this._facetValueFor(e.target);
    dc.app.searcher.removeFromSearch(facet.category);
    return false;
  },

  _loadFacet : function(e) {
    $(e.target).html('loading &hellip;');
    dc.app.searcher.loadFacet($(e.target).attr('data-category'));
  },

  _showLess : function(e) {
    var cat = $(e.target).attr('data-category');
    this._facets[cat].splice(6);
    this.renderFacets(this._facets, 5, this._docCount);
  },

  _showPages : function(e) {
    var el        = $(e.target).closest('.row');
    var kind      = el.attr('data-category');
    var value     = el.attr('data-value');
    var active    = el.hasClass('active');
    var fetch     = _.bind(function() {
      dc.model.Entity.fetch(kind, value, this._connectExcerpts);
    }, this);
    var next = fetch;
    if (!active) {
      var facet = this._facetValueFor(el);
      next = _.bind(function() {
        dc.app.searcher.addToSearch(facet.category, facet.value, fetch);
      }, this);
    }
    dc.app.paginator.ensureRows(next);
    return false;
  },

  _connectExcerpts : function(entities) {
    var sets = _.reduce(entities, function(memo, ent) {
      var docId = ent.get('document_id');
      memo[docId] = memo[docId] || [];
      memo[docId].push(ent);
      return memo;
    }, {});
    _.each(sets, function(set) {
      Documents.get(set[0].get('document_id')).pageEntities.reset(set);
    });
  },

  extractEntities : function(query) {
    var entities = dc.app.visualSearch.searchQuery.filter(_.bind(function(facet) {
      return _.include(this.ENTITY_KINDS, facet.get('category'));
    }, this));
    return _.sortBy(_.map(entities, function(ent) {
      return {type: ent.get('category'), value: ent.get('value')};
    }), function(ent) {
      return ent.value.toLowerCase();
    }).reverse();
  }

});

dc.ui.TimelineDialog = dc.ui.Dialog.extend({

  GRAPH_OPTIONS : {
    xaxis     : {mode : 'time', minTickSize: [1, "day"]},
    yaxis     : {ticks: [], min: -0.5},
    selection : {mode : 'x', color: '#09f'},
    legend    : {show : false},
    series    : {lines : {show : false}, points : {show : true, radius: 4, fill: true, fillColor: 'rgba(255, 255, 235, 0.7)'}},
    grid      : {backgroundColor: '#fff', tickColor: '#ddd', borderWidth: 0, hoverable : true, clickable : true}
  },

  ROW_HEIGHT : 50,
  MIN_HEIGHT : 100,

  DATE_FORMAT : "%b %d, %y",

  POINT_COLOR : '#555',

  id : 'timeline_dialog',

  events : {
    'click .zoom_out':              '_zoomOut',
    'click .ok':                    'confirm',
    'plothover .timeline_plot':     '_showTooltop',
    'plotselected .timeline_plot':  '_zoomIn',
    'plotclick .timeline_plot':     '_openPage'
  },

  constructor : function(documents) {
    this.documents = documents;
    dc.ui.Dialog.call(this, {
      mode        : 'custom',
      title       : this.displayTitle(),
      information : _.t('timeline_zoom_in')
    });
    dc.ui.spinner.show();
    this._loadDates();
  },

  render : function() {
    dc.ui.Dialog.prototype.render.call(this);
    this.$('.custom').html(JST['document/timeline']({docs : this.documents, minHeight : this.MIN_HEIGHT, rowHeight : this.ROW_HEIGHT}));
    this._zoomButton = this.make('span', {'class' : 'minibutton zoom_out dark not_enabled'}, _.t('zoom_out'));
    this.addControl(this._zoomButton);
    this.center();
    return this;
  },

  displayTitle : function() {
    if (this.documents.length == 1 )
      return _.t('timeline_for_doc', dc.inflector.truncate(this.documents[0].get('title'), 55) );
    else
      return _.t('timeline_for_x_docs', this.documents.length );
  },

  // Redraw the Flot Plot.
  drawPlot : function() {
    $.plot(this.$('.timeline_plot'), this._data, this._options);
  },

  _loadDates : function() {
    var dates = _.pluck(this.documents, 'id');
    $.getJSON('/documents/dates', {'ids[]' : dates}, _.bind(this._plotDates, this));
  },

  // Chart the dates for the selected documents.
  _plotDates : function(resp) {
    dc.ui.spinner.hide();
    if (resp.dates.length == 0) return this._noDates();
    this.render();
    var color = this.POINT_COLOR;
    var series = {}, styles = {}, ids = this._dateIds = {};
    _.each(this.documents, function(doc, i) {
      ids[doc.id]      = {};
      series[doc.id]   = [];
      styles[doc.id]   = {pos : i, color : color};
    });
    _.each(resp.dates, function(json) {
      var docId         = json.document_id;
      var date          = json.date * 1000;
      ids[docId][date]  = json.id;
      series[docId].push([date, styles[docId].pos]);
    });
    this._data = _.map(series, function(val, key) {
      return {data : val, color : styles[key].color, docId : parseInt(key, 10)};
    });
    this._options = _.clone(this.GRAPH_OPTIONS);
    this._options.xaxis.min   = null;
    this._options.xaxis.max   = null;
    this._options.yaxis.max   = this.documents.length - 0.5;
    this._options.yaxis.ticks = _.map(this.documents, function(doc){
      return [styles[doc.id].pos, dc.inflector.truncate(doc.get('title'), 30)];
    });
    this.drawPlot();
  },

  // Create a tooltip to show a hovered date.
  _showTooltop : function(e, pos, item) {
    this._pos = pos;
    if (this._request) return;
    if (!item) return dc.ui.tooltip.hide();
    var docId = item.series.docId;
    var id    = this._dateIds[docId][item.datapoint[0]];
    var model = EntityDates.get(id);
    if (model) return this._renderTooltip(model);
    this._request = $.getJSON('/documents/dates', {id : id}, _.bind(function(resp) {
      delete this._request;
      if (!resp) return;
      var model   = new dc.model.Entity(resp.date);
      EntityDates.add(model);
      this._renderTooltip(model);
    }, this));
  },

  _renderTooltip : function(date) {
    var title   = dc.inflector.truncate(Documents.get(date.get('document_id')).get('title'), 45);
    var excerpt = date.get('excerpts')[0];
    dc.ui.tooltip.show({
      left  : this._pos.pageX,
      top   : this._pos.pageY,
      title : title,
      text  : '<b>p.' + excerpt.page_number + '</b> ' + dc.inflector.trimExcerpt(excerpt.excerpt)
    });
  },

  // Allow clicking on date ranges to jump to the page containing the date
  // in the document.
  _openPage : function(e, pos, item) {
    if (!item) return;
    var unixTime = item.datapoint[0] / 1000;
    var doc = Documents.get(item.series.docId);
    window.open(doc.viewerUrl() + "?date=" + unixTime);
  },

  // Allow selection of date ranges to zoom in.
  _zoomIn : function(e, ranges) {
    $(this._zoomButton).setMode('is', 'enabled');
    this._options.xaxis.min = ranges.xaxis.from;
    this._options.xaxis.max = ranges.xaxis.to;
    this.drawPlot();
  },

  // Zoom back out to see the entire timeline.
  _zoomOut : function() {
    $(this._zoomButton).setMode('not', 'enabled');
    this._options.xaxis.min = null;
    this._options.xaxis.max = null;
    this.drawPlot();
  },

  // Close, with an error, when no dates are found.
  _noDates : function() {
    var count = this.documents.length;
    this.close();
    dc.ui.Dialog.alert( _.t('no_dates_for_timeline', count, this.documents[0].get('title') ) );
  }

});

dc.ui.Organizer = Backbone.View.extend({

  id : 'organizer',

  PRIVATE_SEARCHES: [
    'all_documents', 'your_documents', 'your_published_documents'
  ],

  PUBLIC_SEARCHES: [
    'all_documents', 'annotated_documents', 'published_documents', 'popular_documents'
  ],

  events : {
    'click #new_project'              : 'promptNewProject',
    'click .all_documents'            : 'showAllDocuments',
    'click .your_documents'           : 'showYourDocuments',
    'click .org_documents'            : 'showOrganizationDocuments',
    'click .annotated_documents'      : 'showAnnotatedDocuments',
    'click .published_documents'      : 'showPublishedDocuments',
    'click .popular_documents'        : 'showPopularDocuments',
    'click .your_published_documents' : 'showYourPublishedDocuments',
    'click .account_links .text_link' : 'showAccountDocuments',
    'click .toggle_account_links'     : 'toggleAccountLinks',
    'click .organization.box'         : 'showOtherOrgDocuments'
  },
  
  initialize: function(options) {
    _.bindAll(this, '_addSubView', '_removeSubView', 'renderAccounts');
    this._bindToSets();
    this.subViews = [];
  },

  render : function() {
    var searches = dc.account ? this.PRIVATE_SEARCHES : this.PUBLIC_SEARCHES;
    $(this.el).append(JST['organizer/sidebar']({searches : searches}));
    this.projectInputEl = this.$('#project_input');
    this.projectList    = this.$('.project_list');
    this.sidebar        = $('#sidebar');
    this.renderAccounts();
    this.renderAll();
    return this;
  },

  renderAll : function() {
    if (dc.account) {
      if (Projects.isEmpty()) this.setMode('no', 'projects');
      Projects.each(this._addSubView);
    } else {
      this.$('.organization_list').html(JST['organizer/organizations']({organizations: Organizations}));
    }
  },

  renderAccounts : function() {
    _.each( this.$('.account_links'), function(el){
      el = $(el);
      el.html( JST['organizer/account_links']( { organization: Organizations.get( el.attr('data-cid') )} )  );
    });
  },

  promptNewProject : function() {
    var me = this;
    dc.ui.Dialog.prompt(_.t('create_new_project'), '', function(title, dialog) {
      title = dc.inflector.trim(title);
      if (!title) {
        dialog.error( _.t('must_have_title') );
        return;
      }
      if (Projects.find(title)) return me._warnAlreadyExists(title);
      var count = _.inject(Documents.selected(), function(memo, doc){ return memo + doc.get('annotation_count'); }, 0);
      Projects.create({
        title             : title,
        annotation_count  : count,
        document_ids      : Documents.selectedIds(),
        owner             : true
      }, {wait: true});
      return true;
    }, {mode : 'short_prompt'});
  },

  highlight : function(projectName, groupName) {
    Projects.deselectAll();
    this.$('.organization').removeClass('is_selected');
    if (dc.account) {
      var project = projectName && Projects.find(projectName);
      if (project) return project.set({selected : true});
    } else {
      var org = groupName && Organizations.findBySlug(groupName);
      if (org) {
        this.$('#organization_' + org.id).addClass('is_selected');
      }
    }
  },

  showAllDocuments : function() {
    dc.app.searcher.search('');
  },

  showYourDocuments : function() {
    Accounts.current().openDocuments();
  },

  showAnnotatedDocuments : function() {
    dc.app.searcher.search(_.t('filter') + ': annotated');
  },

  showPublishedDocuments : function() {
    dc.app.searcher.search(_.t('filter') + ': published');
  },

  showPopularDocuments : function() {
    dc.app.searcher.search(_.t('filter') + ': popular');
  },

  showAccountDocuments : function(e) {
    var cid = $(e.target).attr('data-cid');
    Accounts.get(cid).openDocuments();
  },

  showYourPublishedDocuments : function() {
    Accounts.current().openDocuments({published : true});
  },

  showOrganizationDocuments : function(e) {
    $(e.target).closest('.organization').toggleClass('show_accounts');
    var cid = $(e.target).parent().find(".account_links").attr("data-cid");
    Organizations.get(cid).openDocuments();
  },

  showOtherOrgDocuments : function(e) {
    var el = $(e.currentTarget);
    Organizations.get(el.attr('data-id')).openDocuments();
  },

  // display the list of accounts for an organization
  toggleAccountLinks : function(e) {
    $(e.target).closest('div.organization').toggleClass('show_accounts');
  },

  // Bind all possible and Project events for rendering.
  _bindToSets : function() {
    Projects.bind('add',     this._addSubView);
    Projects.bind('remove',  this._removeSubView);
    Accounts.bind('all',     this.renderAccounts);
  },

  _warnAlreadyExists : function(title) {
    dc.ui.notifier.show({text : _.t('project_exists', title) });
    return false;
  },

  _addSubView : function(model) {
    this.setMode('has', 'projects');
    var view = new dc.ui.Project({model : model}).render();
    this.subViews.push(view);
    var index         = Projects.indexOf(view.model);
    var previous      = Projects.at(index - 1);
    var previousView  = previous && previous.view;
    if (index == 0 || !previous || !previousView) {
      $(this.projectList).prepend(view.el);
    } else {
      $(previousView.el).after(view.el);
    }
    dc.app.scroller.checkLater();
  },

  _removeSubView : function(model) {
    this.subViews = _.without(this.subViews, model.view);
    $(model.view.el).remove();
    dc.app.scroller.checkLater();
  }

});

