dc.ui.Paginator = Backbone.View.extend({

  DEFAULT_PER_PAGE : 10,
  MINI_PER_PAGE    : 30,

  SORT_TEXT : {
    score       : _.t('by_relevance'),
    title       : _.t('by_title'),
    created_at  : _.t('by_date'),
    source      : _.t('by_source'),
    page_count  : _.t('by_length')
  },

  id        : 'paginator',
  className : 'interface',

  query : null,
  page  : null,
  view  : null,

  events : {
    'click .arrow.left':          'previousPage',
    'click .arrow.right':         'nextPage',
    'click .current_placeholder': 'editPage',
    'change .current_page':       'changePage',
    'click .sorter':              'chooseSort'
  },

  constructor : function(options) {
    Backbone.View.call(this, options);
    this.setSize(dc.app.preferences.get('paginator_mini') || false);
    this.sortOrder = dc.app.preferences.get('sort_order') || 'score';
  },

  setQuery : function(query, view) {
    this.query = query;
    this.page  = query.page;
    $(document.body).addClass('paginated');
    this.render();
  },

  setSortOrder : function(order) {
    this.sortOrder = order;
    dc.app.preferences.set({sort_order : order});
    this.$('.sorter').text(this.SORT_TEXT[this.sortOrder]);
    dc.app.searcher.loadPage();
  },

  queryParams : function() {
    var params = {
      per_page : dc.app.paginator.pageSize(),
      order    : dc.app.paginator.sortOrder
    };
    if (!this.mini) params.mentions = Documents.NUM_MENTIONS;
    return params;
  },

  hide : function() {
    $(document.body).removeClass('paginated');
  },

  // Keep in sync with search.rb on the server.
  pageSize : function() {
    return this.mini ? this.MINI_PER_PAGE : this.DEFAULT_PER_PAGE;
  },

  pageFactor : function() {
    return this.mini ? this.MINI_PER_PAGE / this.DEFAULT_PER_PAGE :
                       this.DEFAULT_PER_PAGE / this.MINI_PER_PAGE;
  },

  pageCount : function() {
    return Math.ceil(this.query.total / this.pageSize());
  },

  render : function() {
    this.setMode('not', 'editing');
    var el = $(this.el);
    el.html('');
    if (!this.query) return this;
    el.html(JST['workspace/paginator']({
      q           : this.query,
      sort_text   : this.SORT_TEXT[this.sortOrder],
      per_page    : this.pageSize(),
      page_count  : this.pageCount()
    }));
    return this;
  },

  setSize : function(mini) {
    this.mini = mini;
    $(document.body).toggleClass('minidocs', this.mini);
  },

  ensureRows : function(callback, doc) {
    if (this.mini) {
      this.toggleSize(callback, doc);
    } else {
      callback();
    }
  },

  toggleSize : function(callback, doc) {
    this.setSize(!this.mini);
    dc.app.preferences.set({paginator_mini : this.mini});
    callback = _.isFunction(callback) ? callback : null;
    var page = Math.floor(((this.page || 1) - 1) / this.pageFactor()) + 1;
    if (doc) page += Math.floor(Documents.indexOf(doc) / this.pageSize());
    dc.app.searcher.loadPage(page, callback);
  },

  chooseSort : function() {
    var dialog = dc.ui.Dialog.choose( _.t('sort_documents_by')+'&hellip;', [
      {text : _.t('relevance'),     value : 'score',      selected : this.sortOrder == 'score'},
      {text : _.t('date_uploaded'), value : 'created_at', selected : this.sortOrder == 'created_at'},
      {text : _.t('title'),         value : 'title',      selected : this.sortOrder == 'title'},
      {text : _.t('source'),        value : 'source',     selected : this.sortOrder == 'source'},
      {text : _.t('length'),        value : 'page_count', selected : this.sortOrder == 'page_count'}
    ], _.bind(function(order) {
      this.setSortOrder(order);
      return true;
    }, this), {mode : 'short_prompt'});
    $(dialog.el).addClass('short_choice');
  },

  editPage : function() {
    this.setMode('is', 'editing');
    this.$('.current_page').focus();
  },

  previousPage : function() {
    var page = (this.page || 1) - 1;
    dc.app.searcher.loadPage(page);
  },

  nextPage : function() {
    var page = (this.page || 1) + 1;
    dc.app.searcher.loadPage(page);
  },

  changePage : function(e) {
    var page = parseInt($(e.target).val(), 10);
    if (page == this.page) return;
    dc.app.searcher.loadPage(page);
  }

});

dc.ui.SearchEmbedDialog = dc.ui.Dialog.extend({

  events : {
    'click .preview'        : 'preview',
    'change select'         : 'update',
    'click select'          : 'update',
    'keyup input'           : 'update',
    'focus input'           : 'update',
    'click input'           : 'update',
    'change input'          : 'update',
    'blur .per_page'        : '_validatePerPage',
    'click .next'           : 'nextStep',
    'click .previous'       : 'previousStep',
    'click .close'          : 'close',
    'click .snippet'        : 'selectSnippet',
    'click .change_access'  : 'changeAccess',
    'click .remove_lines'   : 'removeLines'
  },

  totalSteps : 2,

  STEPS : [null,
           _.t('embed_search_step_one'),
           _.t('embed_search_step_two')
  ],

  DEMO_ERROR : _.t('embed_search_demo_error','<a href="/contact">', '</a>',
                   '<a href="/help/publishing#step_4">','</a>.'),

  DEFAULT_OPTIONS : {
    order      : 'title',
    per_page   : 12,
    search_bar : true,
    title      : null,
    q          : ''
  },

  constructor : function(docs) {
    this.currentStep = 1;
    this.docs        = docs;
    if (docs.length) {
      this.query = _.map(docs, function(doc){ return 'document: ' + doc.id; }).join(' ');
    } else {
      this.query = dc.app.searcher.publicQuery() || "";
    }

    dc.ui.Dialog.call(this, {mode : 'custom', title : this.displayTitle()});
    dc.ui.spinner.show();
    this.fetchCounts();
  },

  fetchCounts : function() {
    $.ajax({
      url  : '/search/restricted_count.json',
      data : {q: this.query},
      dataType : 'json',
      success : _.bind(function(resp) {
        this.restrictedCount = resp.restricted_count;
        this.documentsCount  = this.docs.length || dc.app.paginator.query.total;
        this.publicCount     = this.documentsCount - this.restrictedCount;
        this.render();
      }, this)
    });
  },

  render : function() {
    if (dc.account.organization().get('demo')) return dc.ui.Dialog.alert(this.DEMO_ERROR);
    dc.ui.Dialog.prototype.render.call(this);
    this.$('.custom').html(JST['workspace/search_embed_dialog']({
      query           : this.query,
      projectQuery    : dc.app.visualSearch.searchQuery.has('project'),
      restrictedCount : this.restrictedCount,
      documentsCount  : this.documentsCount,
      publicCount     : this.publicCount
    }));
    this._next          = this.$('.next');
    this._previous      = this.$('.previous');
    this._orderEl       = this.$('select[name=order]');
    this._perPageEl     = this.$('input[name=per_page]');
    this._titleEl       = this.$('input[name=title]');
    this._searchBarEl   = this.$('input[name=search_bar]');
    this._loadPreferences();
    this.setMode('embed', 'dialog');
    this.setMode('search_embed', 'dialog');
    this.update();
    this.setStep();
    this.center();
    dc.ui.spinner.hide();
    return this;
  },

  displayTitle : function() {
    return this.STEPS[this.currentStep];
  },

  preview : function() {
    var options = JSON.stringify(this.embedOptions());
    var params = $.param({
        q       : this.query,
        slug    : dc.inflector.sluggify(this.query),
        options : options
    });
    var url = 'http://' + window.location.host + '/search/preview?' + params;
    window.open(url);
    return false;
  },

  update : function() {
    this._renderPerPageLabel();
    this._renderEmbedCode();
    this._savePreferences();
  },

  embedOptions : function() {
    var options = {};
    options.q            = this.query.replace(/\"/g, '\\\"');
    options.container    = '#DC-search-' + dc.inflector.sluggify(this.query);
    options.title        = this._titleEl.val().replace(/\"/g, '\\\"');
    options.order        = this._orderEl.val();
    options.per_page     = this._perPageEl.val();
    options.search_bar   = this._searchBarEl.is(':checked');
    options.organization = dc.account.organization().id;
    return options;
  },

  // Remove line breaks from the viewer embed.
  removeLines : function() {
    this.$('.snippet').val(this.$('.snippet').val().replace(/[\r\n]/g, ''));
  },

  _savePreferences : function() {
    dc.app.preferences.set({search_embed_options : JSON.stringify(this.embedOptions())});
  },

  _deletePreferences : function() {
    dc.app.preferences.remove('search_embed_options');
  },

  _loadPreferences : function() {
    var options = _.extend({}, this.DEFAULT_OPTIONS, JSON.parse(dc.app.preferences.get('search_embed_options')));
    this._orderEl.val(options.order);
    this._perPageEl.val(options.per_page);
    this._searchBarEl.attr('checked', !!options.search_bar);
  },

  _renderEmbedCode : function() {
    var options          = this.embedOptions();
    options.title        = '"' + options.title + '"';
    options.container    = '"' + options.container + '"';
    options.q            = '"' + options.q + '"';
    options.order        = '"' + options.order + '"';
    var serialized       = _.map(options, function(value, key){ return key + ': ' + value; });
    this.$('.publish_embed_code').html(JST['search/embed_code']({
      query: dc.inflector.sluggify(this.query),
      options: serialized.join(',\n    ')
    }));
  },

  _renderPerPageLabel : function() {
    var perPage = this._perPageEl.val();
    var $label  = this.$('.publish_option_perpage_sidelabel');
    var label;

    if (!perPage || !parseInt(perPage, 10)) {
      label = '&nbsp;';
    } else {
      var pages = Math.max(1, Math.ceil(this.publicCount / perPage));
      var label = [
        _.t('x_documents', this.publicCount),
        ' / ',
        _.t('x_pages', pages )
      ].join('');
    }

    $label.html(label);
  },

  _validatePerPage : function() {
    var $perPage = this.$('input[name=per_page]');
    var perPage  = $perPage.val();

    if (perPage.length == 0) perPage = this.DEFAULT_OPTIONS['per_page'];
    if (perPage <= 0)        perPage = this.DEFAULT_OPTIONS['per_page'];
    if (perPage > 100)       perPage = 100;

    $perPage.val(perPage);
  },

  nextStep : function() {
    this.currentStep += 1;
    if (this.currentStep > this.totalSteps) return this.close();
    if (this.currentStep == 2) this._renderEmbedCode();
    this.setStep();
  },

  previousStep : function() {
    if (this.currentStep > 1) this.currentStep -= 1;
    this.setStep();
  },

  setStep : function() {
    this.title(this.displayTitle());

    this.$('.publish_step').setMode('not', 'enabled');
    this.$('.publish_step_'+this.currentStep).setMode('is', 'enabled');
    this.info( _.t('step_x_of_x', this.currentStep, this.totalSteps), true);

    var first = this.currentStep == 1;
    var last = this.currentStep == this.totalSteps;

    this._previous.setMode(first ? 'not' : 'is', 'enabled');
    this._next.html(last ? _.t('finish') : _.t('next') + ' &raquo;').setMode('is', 'enabled');
  },

  selectSnippet : function() {
    this.$('.snippet').select();
  },

  changeAccess : function() {
    var restrictedQuery = this.query;
    if (restrictedQuery.indexOf('filter:restricted') == -1) {
      restrictedQuery += ' filter:restricted';
    }
    dc.app.searcher.search(restrictedQuery);
    this.close();
  }

});

dc.ui.SubtemplateDataDialog = dc.ui.Dialog.extend({

  id                : 'edit_subtemplate_dialog',
  className         : 'dialog tempalog',
  template          : null,
  parentTemplate    : null,
  fieldViewList     : [],

  dataEvents : {
      'click .cancel'     : 'close',
      'click .ok'         : 'saveAndClose',
      'focus input'       : '_addFocus',
      'focus textarea'    : '_addFocus',
      'blur input'        : '_removeFocus',
      'blur textarea'     : '_removeFocus',
      'change input'      : '_markChanged'
  },


  constructor : function(subtemplate, parentTemplate) {
    this.events         = _.extend({}, this.events, this.dataEvents);
    this.template       = subtemplate;
    this.parentTemplate = parentTemplate;
    this._mainJST = JST['template/subtemplate_dialog'];
    _.bindAll(this, 'render', 'createFieldViews', 'createFieldView', 'createFieldViewsAndRender',
        'saveAndClose', 'showErrors', 'removeFieldViewFromList');
    dc.ui.Dialog.call(this, {mode : 'custom', title : _.t('edit_subtemplate'), saveText : _.t('save') });
    this.template.on('invalid', this.showErrors);

    _thisView = this;

    //Clear fieldViewList in case pointers persist
    this.fieldViewList.length = 0;

    //Fetch parent and/or subtemplate fields if they haven't been pulled; otherwise, render as normal
    if(this.parentTemplate.template_fields.length == 0 && this.template.subtemplate_fields.length == 0 && subtemplate.id != null) {
        this.parentTemplate.fetchFields(function(){
            _thisView.template.fetchFields(_thisView.createFieldViewsAndRender);
        });
    } else if(this.parentTemplate.template_fields.length == 0) {
        this.parentTemplate.fetchFields(this.createFieldViewsAndRender);
    } else if(this.template.subtemplate_fields.length == 0 && subtemplate.id != null) {
        this.template.fetchFields(this.createFieldViewsAndRender);
    } else {
        this.createFieldViewsAndRender();
    }

    $(document.body).append(this.el);
  },


  render : function() {
    //Base dialog object needs
    dc.ui.Dialog.prototype.render.call(this);
    this._container = this.$('.custom');

    //Main template
    this._container.html(this._mainJST({
      name          : this.template.get('sub_name') ? this.template.get('sub_name').replace(/\"/g,'&quot;') : ''
    }));

    //Field listings
      this.$('.field_list').append(_.map( this.fieldViewList, function(view, cid){
          view.render();
          return view.$el;
      }, this));

    return this;
  },


  createFieldViewsAndRender: function() {
     this.createFieldViews();
     this.render();
  },


  createFieldViews: function() {
      _thisView = this;
      this.parentTemplate.template_fields.each(function(field) {
          _thisView.createFieldView(field);
      }, _thisView);
  },


  createFieldView: function(field) {
      _selected = false;
      _fieldMatches = this.template.subtemplate_fields.where({field_id: field.id});
      if( _fieldMatches != null && _fieldMatches.length > 0 ){ _selected = true; }
      _fieldView = new dc.ui.SubtemplateFieldListing({model: field, selected: _selected}, this);
      this.fieldViewList.push(_fieldView);
      _fieldView.on('fieldClicked', this.changeFieldStatus, this);
      return _fieldView;
  },


  //Responds to event of field view being deleted by removing from list.
  //It is passed the initiating view from the event trigger.
  removeFieldViewFromList: function(view) {
      this.fieldViewList.splice(this.fieldViewList.indexOf(view), 1);
  },


  save : function(success) {
    _thisView = this;

    //Clear error class from all inputs
    $('input').removeClass('error');

    //Push template name from view to model
    this.template.set({sub_name: this.$('#template_name').val()});

    //Trigger save
    this.template.save({},{success: success});
  },


  saveAndClose: function() {
    _thisView = this;
    this.save(function(){
        _thisView.close();
    });
  },


  //Handler for errors returned from model validation
  showErrors: function(model, errors) {
    //Handle first error only
    if(errors[0].class == 'sub_name'){ this.$('#template_name').addClass('error'); }

    return this.error(errors[0].message);
  },


  // On change, mark input field as dirty.
  _markChanged : function(e) {
      $(e.target).addClass('change');
  },


  //Change subtemplate field status and update collection. Expected arguments passed in:
  //  *field_id
  //  *action ('add' or 'delete')
  changeFieldStatus: function(args) {
      _thisView = this;
      //If subtemplate hasn't been saved, do that then return
      if(this.template.id == null){
          _thisView.save(function(){ _thisView.changeFieldStatus(args); });
      }
      else {
          _fieldId = parseInt(_fieldId);
          if (args.action == 'add') {
              _newModel = new dc.model.SubtemplateField({
                  template_id: this.parentTemplate.id,
                  subtemplate_id: this.template.id,
                  field_id: _fieldId
              });
              _newModel.save();
              this.template.subtemplate_fields.add(_newModel);
          } else if (args.action == 'delete') {
              _matchingFields = this.template.subtemplate_fields.where({field_id: _fieldId});
              if (_matchingFields != null && _matchingFields.length > 0) {
                  _matchingFields[0].destroy();
              }
          }
      }
  }


}, {

  // This static method is used for conveniently opening the dialog for
  // any selected template.
  open : function(subtemplate, parentTemplate) {
    new dc.ui.SubtemplateDataDialog(subtemplate, parentTemplate);
  }

});

