// A simple iframe-based downloader that can fire a callback when the download
// has begun.
dc.app.download = function(url, callback) {
  dc.ui.spinner.show();
  var iframe = document.createElement('iframe');
  iframe.src = url;
  iframe.style.display = 'none';
  // webkit's readyState will be complete immediatly
  // because the zip file is not "rendered" by the browser
  // https://code.google.com/p/chromium/issues/detail?id=32333#c16
  var timer = setInterval(function(){
    // firefox =='complete' ie == 'interactive'
    if (iframe.contentDocument.readyState == 'complete' || iframe.contentDocument.readyState == 'interactive') {
      dc.ui.spinner.hide();
      clearInterval(timer);
      if (callback) callback();
    }
  }, 5000);
  // if we remove the iframe before the zip finishes,
  // the download is canceled.
  // 15 minutes *should* be enough for everyone
  _.delay(function(){
    $(iframe).remove();
  },900000);
  $(document.body).append(iframe);
};

// Main controller for the in-viewer document editor. Orchestrates subviews.
dc.app.editor = new Backbone.View();
_.extend(dc.app.editor, {

  templateList: null, //Reference list of all templates
  templateArray: [],

  // Initializes the workspace, binding it to <body>.
  initialize : function(docId, options) {
    this.setElement('body');
    this.docId = docId;
    this.options = options;
    _.bindAll(this, 'closeAllEditors', 'confirmStateChange');
    this.templateList = options.templateList;
    this.flattenTemplateList(options.templateList);
    dc.app.hotkeys.initialize();
    this.createSubViews();
    this.renderSubViews();
    currentDocument.api.setWPDLink(options.wpd_server);
    currentDocument.api.onChangeState(this.closeAllEditors);
  },

  confirmStateChange : function(continuation) {
    if (this._openDialog) return;
    this._openDialog = dc.ui.Dialog.confirm('You have unsaved changes. Are you sure you want to leave without saving them?', continuation, {onClose : _.bind(function() {
      this._openDialog = null;
    }, this)});
  },

  setSaveState : function(unsaved) {
    this.unsavedChanges = unsaved;
    var confirmation = unsaved ? this.confirmStateChange : null;
    currentDocument.api.setConfirmStateChange(confirmation);
  },

  // Create all of the requisite subviews.
  createSubViews : function() {
    dc.ui.notifier          = new dc.ui.Notifier();
    this.sectionEditor      = new dc.ui.SectionEditor();
    this.annotationEditor   = new dc.ui.AnnotationEditor();

    switch(this.options.status) {
      case 2:
      case 3:
          this.controlPanel = new dc.ui.ViewerDEControlPanel();
          break;
      case 5:
          this.controlPanel = new dc.ui.ViewerQCControlPanel();
          break;
      case 7:
          this.controlPanel = new dc.ui.ViewerQAControlPanel();
          break;
      case 8:
          this.controlPanel = new dc.ui.ViewerExtractControlPanel();
          break;
      case 10:
          this.controlPanel = new dc.ui.ViewerSuppDEControlPanel();
          break;
      case 12:
          this.controlPanel = new dc.ui.ViewerSuppQCControlPanel();
          break;
      case 14:
          this.controlPanel = new dc.ui.ViewerSuppQAControlPanel();
          break;
      default:
          alert('System Error: Invalid document status for editing');
    }

    this.removePagesEditor  = new dc.ui.RemovePagesEditor({editor : this});
    this.reorderPagesEditor = new dc.ui.ReorderPagesEditor({editor : this});
    this.editPageTextEditor = new dc.ui.EditPageTextEditor({editor : this});
    this.replacePagesEditor = new dc.ui.ReplacePagesEditor({editor : this});
  },

  // Render all of the existing subviews and place them in the DOM.
  renderSubViews : function() {
    var access = 'DV-isContributor';
    if (this.options.isReviewer) access = 'DV-isReviewer';
    if (this.options.isOwner) access = 'DV-isOwner';
    $('.DV-docViewer').addClass(access);
    $('.DV-well').append(this.controlPanel.el);
    $('.DV-logo').hide();
    $('.DV-thumbnailsView').show();
    currentDocument.api.roundTabCorners();
    var supp = $('.DV-supplemental');
    if (supp.hasClass('DV-noNavigation')) {
      supp.removeClass('DV-noNavigation').addClass('DV-noNavigationMargin');
    }
  },

  closeAllEditors : function() {
    this.removePagesEditor.close();
    this.reorderPagesEditor.close();
    this.replacePagesEditor.close();
    this.editPageTextEditor.close();
    return true;
  },

  //Turn template JSON into strings indicating lineage
  flattenTemplateList: function(templateJSON){
    var _thisRef = this;
    $(_thisRef.templateList).each(function(index, template) {
      _thisRef.templateArray.push(template.name);
      $(template.subtemplates).each(function(index, subtemplate){
        _thisRef.templateArray.push(template.name + ' :: ' + subtemplate.sub_name);
      });
    });
  },

  //Convert templateArray string to one or more IDs
  getIDsForTemplateString: function(tmpString){
    var _thisRef = this;
    var templateName = tmpString.split(' :: ')[0];
    var subtemplateName = tmpString.split(' :: ')[1];
    var templateIDs = [null, null];

    for(var i=0; i < this.templateList.length; i++){
      if( this.templateList[i].name == templateName){
        templateIDs[0] = this.templateList[i].id;
        for(var j=0; j < this.templateList[i].subtemplates.length; j++){
          if( this.templateList[i].subtemplates[j].sub_name == subtemplateName ){
            templateIDs[1] = this.templateList[i].subtemplates[j].id;
            break;
          }
        }
        break;
      }
    }

    return templateIDs;
  }

});
// DocumentCloud workspace hotkeys. To tell if a key is currently being pressed,
// just ask `VS.app.hotkeys.[key]` on `keypress`, or ask `VS.app.hotkeys.key(e)`
// on `keydown`.
// 
// For the most headache-free way to use this utility, check modifier keys,
// like shift and command, with `VS.app.hotkeys.shift`, and check every other
// key with `VS.app.hotkeys.key(e) == 'key_name'`.
dc.app.hotkeys = {

  // Keys that will be mapped to the `hotkeys` namespace.
  KEYS: {
    '16':  'shift',
    '17':  'control',
    '91':  'command',
    '93':  'command',
    '224': 'command',
    '13':  'enter',
    '37':  'left',
    '38':  'upArrow',
    '39':  'right',
    '40':  'downArrow',
    '46':  'delete',
    '8':   'backspace',
    '9':   'tab',
    '188': 'comma'
  },

  // Binds global keydown and keyup events to listen for keys that match `this.KEYS`.
  initialize : function() {
    _.bindAll(this, 'down', 'up', 'blur');
    $(document).bind('keydown', this.down);
    $(document).bind('keyup', this.up);
    $(window).bind('blur', this.blur);
  },
  
  // On `keydown`, turn on all keys that match.
  down : function(e) {
    var key = this.KEYS[e.which];
    if (key) this[key] = true;
  },

  // On `keyup`, turn off all keys that match.
  up : function(e) {
    var key = this.KEYS[e.which];
    if (key) this[key] = false;
  },

  // If an input is blurred, all keys need to be turned off, since they are no longer
  // able to modify the document.
  blur : function(e) {
    for (var key in this.KEYS) this[this.KEYS[key]] = false;
  },
  
  // Check a key from a jQuery event and return the common english name.
  key : function(e) {
    return this.KEYS[e.which];
  },
  
  // Colon is special, since the value is different between browsers.
  colon : function(e) {
    var charCode = e.which;
    return charCode && String.fromCharCode(charCode) == ":";
  },
  
  // Check a key from an event and match it against any known characters.
  // The `keyCode` is different depending on the event type: `keydown` vs. `keypress`.
  // These were determined by looping through every `keyCode` and `charCode` that
  // resulted from `keydown` and `keypress` events and counting what was printable.
  printable : function(e) {
    var code = e.which;
    if (e.type == 'keydown') {
      if (code == 32 ||                      // space
          (code >= 48 && code <= 90) ||      // 0-1a-z
          (code >= 96 && code <= 111) ||     // 0-9+-/*.
          (code >= 186 && code <= 192) ||    // ;=,-./^
          (code >= 219 && code <= 222)) {    // (\)'
        return true;
      }
    } else {
      // [space]!"#$%&'()*+,-.0-9:;<=>?@A-Z[\]^_`a-z{|} and unicode characters
      if ((code >= 32 && code <= 126)  ||
          (code >= 160 && code <= 500) ||
          (String.fromCharCode(code) == ":")) {
        return true;
      }      
    }
    return false;
  }

};
// Read and write preferences for the browser, using a cookie.
dc.app.cookies = {

  // Read a cookie by name.
  get : function(name) {
    var matcher = new RegExp("\\s*" + name + "=(.*)$");
    var list    = document.cookie.split(';');
    var cookie  = _.detect(list, function(c) { return c.match(matcher); });
    return cookie ? decodeURIComponent(cookie.match(matcher)[1]) : null;
  },

  // Write a cookie's value, and keep it only for the session (default), or
  // forever (2 years).
  set : function(name, value, keep) {
    var expiration = keep ? new Date() : null;
    if (expiration) keep == 'remove' ? expiration.setYear(expiration.getFullYear() - 1) :
                                       expiration.setYear(expiration.getFullYear() + 2);
    var date = expiration ? '; expires=' + expiration.toUTCString() : '';
    document.cookie = name + '=' + encodeURIComponent(value) + '; path=/' + date;
  },

  // Remove a cookie.
  remove : function(name) {
    this.set(name, this.get(name), 'remove');
  }

};

dc.app.preferences = {

  get : function(name, valid) {
    var value = this._prefs()[name];
    return (!value || (valid && !_.include(valid, value))) ? null : value;
  },

  set : function(obj) {
    this._setPrefs(_.extend(this._prefs(), obj));
  },

  remove : function(name) {
    var prefs = this._prefs();
    delete prefs[name];
    this._setPrefs(prefs);
  },

  _prefs : function() {
    return JSON.parse(dc.app.cookies.get('document_cloud_preferences') || '{}');
  },

  _setPrefs : function(obj) {
    dc.app.cookies.set('document_cloud_preferences', JSON.stringify(obj), true);
  }

};
// The `dc.app.searcher` is the core controller for running document searches
// from the client side. It's main "view" is the dc.ui.SearchBox.
dc.controllers.Searcher = Backbone.Router.extend({

  // Error messages to display when your search returns no results.

 NO_RESULTS : {
  project    : _.t('not_found_project'),
  account    : _.t('not_found_account'),
  group      : _.t('not_found_group'),
   published : _.t('not_found_published'),
  annotated  : _.t('not_found_annotated'),
  search     : _.t('not_found_search'),
  all        : _.t('not_found_all')
 },

  PAGE_MATCHER  : (/\/p(\d+)$/),

  DOCUMENTS_URL : '/search/documents.json',

  FACETS_URL    : '/search/facets.json',

  fragment      : null,

  flags         : {},

  routes        : {
    'search/*query/p:page':  'searchByHash',
    'search/*query':         'searchByHash'
  },

  // Creating a new SearchBox registers #search page fragments.
  initialize : function() {
    this.searchBox = dc.app.searchBox;
    this.flags.hasEntities = false;
    this.currentSearch = null;
    this.titleBox = $('.search_tab_content .title_box_inner');
    _.bindAll(this, '_loadSearchResults', '_loadFacetsResults', '_loadFacetResults',
      'loadDefault', 'loadFacets');
    dc.app.navigation.bind('tab:search', this.loadDefault);
  },

  urlFragment : function() {
    return this.fragment + (this.page ? '/p' + this.page : '');
  },

  // Load the default starting-point search.
  loadDefault : function(options) {
    options || (options = {});
    if (options.clear) {
      Documents.reset();
      this.searchBox.value('');
    }
    if (this.currentSearch) return;
    if (!Documents.isEmpty()) {
      this.navigate(this.urlFragment());
      this.showDocuments();
    } else if (this.searchBox.value()) {
      this.search(this.searchBox.value());
    } else if (dc.account && dc.account.get('hasDocuments')) {
      Accounts.current().openDocuments();
    } else if (Projects.first()) {
      Projects.first().open();
    } else if (options.showHelp && dc.account) {
      dc.app.navigation.open('help');
    } else {
      this.search('');
    }
  },

  // Paginate forwards or backwards in the search results.
  loadPage : function(page, callback) {
    page = page || this.page || 1;
    var max = dc.app.paginator.pageCount();
    if (page < 1) page = 1;
    if (page > max) page = max;
    this.search(this.searchBox.value(), page, callback);
  },

  // Quote a string if necessary (contains whitespace).
  quote : function(string) {
    return string.match(/\s/) ? '"' + string + '"' : string;
  },

  publicQuery : function() {

    // Swap out projects.
    var projects = [];
    var projectNames = dc.app.visualSearch.searchQuery.values('project');
    _.each(projectNames, function(projectName) {
      projects.push(Projects.find(projectName));
    });
    var query = dc.app.visualSearch.searchQuery.withoutCategory('project');
    if ( ! _.isEmpty(projects) ){
      query = _.map(projects, function(p) { return 'projectid: ' + p.slug(); }).join(' ') + ' ' + query;
    }

    // Swap out documents for short ids.
    query = query.replace(/(document: \d+)-\S+/g, '$1');

    return query;
  },

  queryText : function() {
    return dc.app.visualSearch.searchQuery.find('text');
  },

  searchKeySubstitutions: function(){
    if ( dc.app && dc.app.workspace ){
      return this._searchKeys || ( this._searchKeys = _.invert( dc.app.workspace.searchKeySubstitutions  ) );
    } else {
      return {};
    }
  },

  searchValueSubstitutions: function(){
    if ( this._searchValues ){
      return this._searchValues;
    }
    if ( dc.app && dc.app.workspace ){
      var values = {};
      _.each( dc.app.workspace.searchValueSubstitutions, function(value,key) {
        values[ key ] = _.invert( value );
      });
      return this._searchValues = values;
    } else {
      return {};
    }
  },

  // Start a search for a query string, updating the page URL.
  search : function(query, pageNumber, callback) {
    dc.ui.spinner.show();
    dc.app.navigation.open('search');
    if (this.currentSearch) this.currentSearch.abort();
    this.searchBox.value(query);
    this.flags.related  = query.indexOf('related:') >= 0;
    this.flags.specific = query.indexOf('document:') >= 0;
    this.flags.hasEntities = false;
    this.page = pageNumber <= 1 ? null : pageNumber;
    this.fragment = 'search/' + query;
    this.showDocuments();
    this.navigate(this.urlFragment());
    Documents.reset();
    this._afterSearch = callback;

    var keys   = this.searchKeySubstitutions(),
        values = this.searchValueSubstitutions(),
        parts  = [];

    // Turn the translated strings back into the appropriate
    // keys and values to send to the server
    dc.app.visualSearch.searchQuery.each(_.bind(function(facet, i) {
      facet = facet.clone();
      var category = facet.get('category'),
          options  = values[ keys[category] ];
      facet.set({ category: keys[category] || category });
      if ( options ){
        facet.set({ value: options[ facet.get('value') ] || facet.get('value') });
      }
      parts.push(facet.serialize());
    }, this));

    var params = _.extend(dc.app.paginator.queryParams(), {q : parts.join(' ') });

    if (dc.app.navigation.isOpen('entities'))   params.include_facets = true;
    if (this.page)                              params.page = this.page;
    this.currentSearch = $.ajax({
      url:      this.DOCUMENTS_URL,
      data:     params,
      success:  this._loadSearchResults,
      error:    function(req, textStatus, errorThrown) {
        if (req.status == 403) Accounts.forceLogout();
      },
      dataType: 'json'
    });
  },

  showDocuments : function() {
    var query       = this.searchBox.value();
    var title       = dc.model.DocumentSet.entitle(query);
    var projectName = dc.app.visualSearch.searchQuery.find( _.t('project') );
    var groupName   = dc.app.visualSearch.searchQuery.find( _.t('group') );

    $(document.body).setMode('active', 'search');
    this.titleBox.html(title);
    dc.app.organizer.highlight(projectName, groupName);
  },

  // Hide the spinner and remove the search lock when finished searching.
  doneSearching : function() {
    var count      = dc.app.paginator.query.total;

    var searchType = this.searchType();

    if (this.flags.specific) {
      this.titleBox.text( _.t('x_documents', count ) );
    } else if (searchType == 'search') {
      var quote  = dc.app.visualSearch.searchQuery.has( _.t('project') );
      if ( count ){
        this.titleBox.html( _.t('x_results',count) );
      } else {
        this.titleBox.html( _.t('no_results_for', (quote ? '“' : '') + this.titleBox.html() + (quote ? '”' : '') ) );
      }
    }
    if (count <= 0) {
      $(document.body).setMode('empty', 'search');
      var explanation = this.NO_RESULTS[searchType] || this.NO_RESULTS['search'];
      $('#no_results .explanation').text(explanation);
    }
    dc.ui.spinner.hide();
    dc.app.scroller.checkLater();
  },

  searchType : function() {
    var single   = false;
    var multiple = false;

    dc.app.visualSearch.searchQuery.each(function(facet) {
      var category = facet.get('category');
      var value    = facet.get('value');

      if (value) {
        if (!single && !multiple) {
          single = category;
        } else {
          multiple = true;
          single = false;
        }
      }
    });

    if ( single == _.t('filter') ) {
      return dc.app.visualSearch.searchQuery.first().get('value');
    } else if (single == _.t('projectid') ) {
      return 'project';
    } else if (_.contains([_.t('project'), _.t('group'), _.t('account')], single)) {
      return single;
    } else if (!single && !multiple) {
      return 'all';
    }

    return 'search';
  },

  loadFacets : function() {
    if (this.flags.hasEntities) return;
    var query = this.searchBox.value() || '';
    dc.ui.spinner.show();
    this.currentSearch = $.get(this.FACETS_URL, {q: query}, this._loadFacetsResults, 'json');
  },

  loadFacet : function(facet) {
    dc.ui.spinner.show();
    this.currentSearch = $.get(this.FACETS_URL, {q : this.searchBox.value(), facet : facet}, this._loadFacetResults, 'json');
  },

  // When searching by the URL's hash value, we need to unescape first.
  searchByHash : function(query, page) {
    _.defer(_.bind(function() {
      this.search(query ? decodeURIComponent(query) : '', page && parseInt(page, 10));
    }, this));
  },

  // Toggle a query fragment in the search.
  toggleSearch : function(category, value) {
    if (dc.app.visualSearch.searchQuery.has(category)) {
      this.removeFromSearch(category);
    } else {
      this.addToSearch(category, value);
    }
  },

  // Add a query fragment to the search and search again, if it's not already
  // present in the current search.
  addToSearch : function(category, value, callback) {
    if (dc.app.visualSearch.searchQuery.has(category, value)) return;
    dc.app.visualSearch.searchQuery.add({category: category, value: value, app: dc.app.visualSearch});
    var query = dc.app.visualSearch.searchQuery.serialize();
    this.search(query, null, callback);
  },

  // Remove a query fragment from the search and search again, only if it's
  // present in the current search.
  removeFromSearch : function(category) {
    var query = dc.app.visualSearch.searchQuery.withoutCategory(category);
    this.search(query);
    return true;
  },

  viewEntities : function(docs) {
    dc.app.navigation.open('entities', true);
    this.search(_.map(docs, function(doc){ return 'document: ' + doc.canonicalId(); }).join(' '));
  },

  // After the initial search results come back, send out a request for the
  // associated metadata, as long as something was found. Think about returning
  // the metadata right alongside the document JSON.
  _loadSearchResults : function(resp) {
    dc.app.paginator.setQuery(resp.query, this);
    if (resp.facets) this._loadFacetsResults(resp);
    var docs = resp.documents;
    for (var i = 0, l = docs.length; i < l; i++) docs[i].index = i;
    Documents.reset(docs);
    this.doneSearching();
    this.currentSearch = null;
    if (this._afterSearch) this._afterSearch();
  },

  _loadFacetsResults : function(resp) {
    dc.app.workspace.entityList.renderFacets(resp.facets, 5, resp.query.total);
    dc.ui.spinner.hide();
    this.currentSearch = null;
    this.flags.hasEntities = true;
  },

  _loadFacetResults : function(resp) {
    dc.app.workspace.entityList.mergeFacets(resp.facets, 500, resp.query.total);
    dc.ui.spinner.hide();
    this.currentSearch = null;
  }

});

// Main controller for the journalist workspace. Orchestrates subviews.
dc.controllers.Workspace = Backbone.Router.extend({

  routes : {
    'accounts'      : 'accounts',
    'repositories'  : 'repositories',
    'templates'     : 'templates',
    'extraction'    : 'extraction'
  },

  // Initializes the workspace, binding it to <body>.
  initialize : function() {
    this.createSubViews();
    this.renderSubViews();
    dc.app.searcher = new dc.controllers.Searcher;
    if (!Backbone.history.start({pushState : true, root : dc.account ? '/' : '/public/'})) {
      dc.app.searcher.loadDefault({showHelp: false});
    }
  },

  accounts: function() {
    dc.app.accounts.open();
  },

  templates: function() {
    dc.app.templates.open();
  },

  repositories: function() {
    dc.app.repositories.open();
  },

  extraction: function() {
    dc.app.extraction.open();
  },

  // Create all of the requisite subviews.
  createSubViews : function() {
    dc.app.paginator      = new dc.ui.Paginator();
    dc.app.navigation     = new dc.ui.Navigation();
    dc.app.toolbar        = new dc.ui.Toolbar();
    dc.app.organizer      = new dc.ui.Organizer();
    dc.app.repositories   = new dc.ui.RepoManager();
    dc.app.templates      = new dc.ui.TemplateManager();
    dc.app.extraction     = new dc.ui.ExtractionManager();
    dc.ui.notifier        = new dc.ui.Notifier();
    dc.ui.tooltip         = new dc.ui.Tooltip();
    dc.app.visualSearch   = new VS.VisualSearch(this.searchOptions());
    dc.app.searchBox      = dc.app.visualSearch.searchBox;
    dc.i18n               = I18n.noConflict();
    this.sidebar          = new dc.ui.Sidebar();
    this.panel            = new dc.ui.Panel();
    this.documentList     = new dc.ui.DocumentList();
    this.entityList       = new dc.ui.EntityList();

    if (!dc.account) return;
    dc.app.accountSearch          = new VS.VisualSearch(this.accountSearchOptions());
    dc.app.accountSearchBox       = dc.app.accountSearch.searchBox;
    dc.app.accounts               = new dc.ui.AccountManager();
    dc.app.accounts.organizations = new dc.ui.OrganizationList();

    dc.app.uploader        = new dc.ui.UploadDialog();
    dc.app.accounts.dialog = new dc.ui.AccountDialog();
    this.accountBadge      = new dc.ui.AccountView({model : Accounts.current(), kind : 'badge'});
  },

  // Render all of the existing subviews and place them in the DOM.
  renderSubViews : function() {
    var content   = $('#content');
    content.append(this.sidebar.render().el);
    content.append(this.panel.render().el);
    dc.app.navigation.render();
    dc.app.hotkeys.initialize();
    this.help = new dc.ui.Help({el : $('#help')[0]}).render();
    this.panel.add('search_box', dc.app.searchBox.render().el);
    this.panel.add('pagination', dc.app.paginator.el);
    this.panel.add('search_toolbar', dc.app.toolbar.render().el);
    this.panel.add('document_list', this.documentList.render().el);
    this.sidebar.add('entities', this.entityList.render().el);
    $('#no_results_container').html(JST['workspace/no_results']({}));
    this.sidebar.add('organizer', dc.app.organizer.render().el);

    if (!dc.account) return;
    dc.app.accounts.setElement($("#accounts_manager_container"));
    dc.app.accounts.render();
    this.panel.add('account_search_box', dc.app.accountSearchBox.render().el);
    this.sidebar.add('organization_list', dc.app.accounts.organizations.render().el);
    this.sidebar.add('account_badge', this.accountBadge.render().el);
  },

  // Translated Search keys
  searchKeySubstitutions: {
    project:   _.t('project'),
    text:      _.t('text'),
    title:     _.t('title'),
    source:    _.t('source'),
    status:    _.t('status')
  },

  // Translated pre-defined search values
  searchValueSubstitutions:{
    access: {
      'public':     _.t('public'),
      'private':    _.t('private'),
      organization: _.t('organization',1),
      pending:      _.t('pending'),
      error:        _.t('error')
    },
    filter: {
      annotated:   _.t('annotated'),
      popular:     _.t('popular'),
      published:   _.t('published'),
      unpublished: _.t('unpublished'),
      restricted:  _.t('restricted')
    }
  },

  searchOptions : function() {
    // store the translated search key and values locally
    // so they can be referenced inside callbacks
    var keys   = this.searchKeySubstitutions,
        values = this.searchValueSubstitutions;

    return {
      unquotable : [
        keys.text,
        keys.account,
        keys.document,
        keys.filter,
        keys.group,
        keys.access,
        keys.projectid
      ],
      callbacks : {
        search : function(query, facets ) {
          if (!dc.app.searcher.flags.outstandingSearch) {
            dc.app.paginator.hide();
            _.defer(dc.app.toolbar.checkFloat);
            dc.app.searcher.search( query );
          }
          return false;
        },
        focus : function() {
          Documents.deselectAll();
        },

        valueMatches : function(category, searchTerm, cb) {
          switch (category) {
          case keys.account:
              cb(Accounts.map(function(a) { return {value: a.get('slug'), label: a.fullName()}; }));
              break;
            case keys.project:
              cb(Projects.pluck('title'));
              break;
            case keys.filter:
              cb( _.values( values.filter ) );
              break;
            case keys.access:
              cb( _.values( values.access ) );
              break;
            case keys.title:
              cb(_.uniq(Documents.pluck('title')));
              break;
            case keys.source:
              cb(_.uniq(_.compact(Documents.pluck('source'))));
              break;
            case keys.group:
              cb(Organizations.map(function(o) { return {value: o.get('slug'), label: o.get('name') }; }));
              break;
            case keys.document:
              cb(Documents.map(function(d){ return {value: d.canonicalId(), label: d.get('title')}; }));
              break;
            case keys.status:
              cb(dc.app.workspace.documentList.DOC_STATUS);
              break;
            default:
              // Meta data
              cb(_.compact(_.uniq(Documents.reduce(function(memo, doc) {
                if (_.size(doc.get('data'))) memo.push(doc.get('data')[category]);
                return memo;
              }, []))));
          }
        },
        facetMatches : function(cb) {
          var prefixes = [
            { label: keys.project,     category: '' },
            { label: keys.text,        category: '' },
            { label: keys.title,       category: '' },
            { label: keys.source,      category: '' },
            { label: keys.status,      category: '' }
          ];
          var metadata = _.map(_.keys(Documents.reduce(function(memo, doc) {
            if (_.size(doc.get('data'))) _.extend(memo, doc.get('data'));
            return memo;
          }, {})), function(key) {
            return {label: key, category: ''};
          });
          cb && cb(prefixes.concat(metadata));
        }
      }
    };
  },
  
  accountSearchOptions: function() {
    return {
      
    };
  }

});
