window.JST['document/entities'] = _.template('<% var entities = doc.entities; %>\n<% var renderedButton = false; %>\n\n<% var renderList = only ? [only] : dc.model.Entity.ORDER; %>\n<% _.each(renderList, function(kind) { %>\n<% var list  = (entities.index()[kind] || []); %>\n<% var graph = only || _.include(dc.model.Entity.SPARK_GRAPHS, kind); %>\n<% var page  = only ? list : list.slice(0, dc.model.Entity.PER_PAGE); %>\n<% if (list.length) { %>\n<div class="entity_group">\n\n  <div class="entity_group_header">\n    <% if (!renderedButton) { %>\n    <span class="icon cancel_search" title="hide entities"></span>\n    <% renderedButton = true; %>\n    <% } %>\n    <% if (only) { %>\n    <span class="arrow left interface">←</span>\n    <% } %>\n    <span class="entity_group_title interface">\n      <%= list.length + \' \' + (list.length == 1 ? dc.inflector.capitalize(kind) : dc.model.Entity.DISPLAY_NAME[kind]) %>\n    </span>\n    <% if (list.length > page.length) { %>\n    <span class="text_link show_all" data-kind="<%= kind %>"><%= _.t(\'show_all\') %></span>\n    <% } %>\n  </div>\n\n  <% if (graph) { %>\n\n  <% for (var i = 0, l = page.length; i < l; i++) { %>\n  <% var entity = page[i]; %>\n  <div class="entity_line" data-id="<%= entity.id %>">\n    <div class="entity_line_title quiet_link">\n      <%= entity.escape(\'value\') %>\n    </div>\n    <div class="entity_buckets" style="width:<%= width %>px;">\n      <div class="entity_rule"></div>\n      <div>\n        <% var buckets = entity.buckets(width); %>\n        <% for (var j = 0, k = buckets.length + distance; j < k; j++) { %>\n        <% var bucket = buckets[j]; %>\n        <div class="<%= bucket ? \'occurs\' : \'\' %> entity_bucket_wrap">\n          <% if (bucket) { %>\n          <div class="entity_bucket" data-occurrence="<%= bucket.offset + \':\' + bucket.length %>" style="height:<%= bucket.height %>px; margin-top: -<%= Math.floor(bucket.height / 2) %>px;"></div>\n          <% } %>\n        </div>\n        <% } %>\n      </div>\n    </div>\n  </div>\n  <% } %>\n\n  <% } else { %>\n\n  <div class="entity_list" style="width:<%= width + 120 %>px;">\n    <% for (var i = 0, l = page.length; i < l; i++) { %>\n    <% var entity = page[i]; %>\n    <span class="entity_list_title quiet_link" data-id="<%= entity.id %>"><%= entity.escape(\'value\') %></span><%= ((i < l - 1) ? \', \' : \'\') %>\n    <% } %>\n  </div>\n\n  <% } %>\n\n</div>\n<% } %>\n<% }); %>\n');
window.JST['document/mentions'] = _.template('<div class="mentions">\n  <div class="mentions_header">\n    <span class="icon cancel_search" title="hide pages"></span>\n    <span class="mentions_title noselect interface"><%= mentions.length %>\n    <%= total > mentions.length ? \' \' + _.t(\'of\') + \' \' + total : "" %>\n    <%= dc.inflector.pluralize(\'page\', total) %>\n    <%= _.t(\'mentioning_query\', query ) %></span>\n    <% if (total > mentions.length) { %>\n      <span class="text_link noselect show_mentions"><%= _.t(\'show_all_x_pages\', total ) %></span>\n    <% } %>\n  </div>\n  <% _.each(mentions, function(mention) { %>\n    <div class="page mention" data-page="<%= mention.page %>">\n      <div class="page_icon">\n        <img class="page_image" alt="page <%= mention.page %>" src="<%= model.pageThumbnailURL(mention.page) %>" />\n        <div class="page_number"><%= _.t(\'pg\') %> <%= mention.page %></div>\n      </div>\n      <div class="page_excerpt noselect"><%= dc.inflector.trimExcerpt(dc.inflector.squeeze(mention.text)) %></div>\n      <div class="clear"></div>\n    </div>\n  <% }); %>\n  <div class="clear"></div>\n</div>');
window.JST['document/note'] = _.template('<div class="note_contents">\n  <div class="note_title display">\n    <span class="<% if (!disableLinks) { %>title_link<% } %>"><%= note.get(\'title\') %> <span class="page_number">(<%=_.t(\'pg\') %> <%= note.get(\'page\') %>)</span></span>\n  </div>\n  <% if (note.get(\'access\') == \'exclusive\') { %>\n  <div class="note_draft_label interface display"><%= _.t(\'draft\') %></div>\n  <% } %>\n\n  <% if (note.get(\'access\') == \'private\') { %>\n  <div class="icon lock display" title="<%= _.t(\'private_note\') %>"></div>\n  <% } %>\n  <div class="edit_note icon edit_glyph display"></div>\n  <br class="clear display" />\n  <div class="note_title_edit edit text_input light">\n    <div class="inner">\n      <input type="text" class="note_title_input" value="" />\n    </div>\n  </div>\n  <% var coords = note.coordinates(); %>\n  <% if (coords) { %>\n  <div class="note_excerpt_wrap">\n    <div class="note_excerpt" style="height: <%= coords.height %>px;">\n      <div class="left_cover"  style="height: <%= coords.height %>px; width: <%= coords.left %>px;"></div>\n      <div class="right_cover" style="height: <%= coords.height %>px; width: <%= 750 - coords.right %>px;"></div>\n      <img class="note_image" src="<%= note.imageUrl() %>" alt="" style="top: -<%= coords.top %>px;" />\n    </div>\n  </div>\n  <% } %>\n  <div class="note_text display"><%= note.get(\'content\') %></div>\n  <div class="annotation_meta">\n    <% if (note.get(\'author\')) { %>\n    <div class="annotation_author interface display"><%= _.t(\'annotated_by\', note.get(\'author\') ) %><% if (note.get(\'author_organization\')) { %>, <i><%= note.get(\'author_organization\') %><% } %></i></div>\n    <% } %>\n    <% if (note.get(\'access\') == \'exclusive\') { %>\n    <div class="annotation_access interface display">\n      <%= _.t(\'draft_note_visible\') %>\n    </div>\n    <% } else if (note.get(\'access\') == \'private\') { %>\n    <div class="annotation_access interface display">\n      <%= _.t(\'private_note_visible\') %>\n    </div>\n    <% } %>\n  </div>\n  <div class="note_text_edit_wrap">\n    <textarea class="note_text_edit text_area edit"></textarea>\n  </div>\n  <div class="note_controls edit">\n    <div class="minibutton warn delete_note float_left"><%= _.t(\'delete\') %></div>\n    <div class="minibutton default save_note float_right">\n      <% if (note.get(\'access\') == \'exclusive\') { %>\n      <%= _.t(\'publish\') %>\n      <% } else { %>\n      <%= _.t(\'save\') %>\n      <% } %>\n    </div>\n    <% if (_.contains([\'public\', \'exclusive\'], note.get(\'access\'))) { %>\n    <div class="minibutton save_draft_note float_right"><%= _.t(\'save_as_draft\') %></div>\n    <% } %>\n    <div class="minibutton cancel_note float_right"><%= _.t(\'cancel\') %></div>\n  </div>\n</div>\n');
window.JST['document/note_select'] = _.template('<% var notes = doc.notes.unrestricted(); %>\n<% if (notes.length) { %>\n  <select class="note_select">\n    <% _.each(doc.notes.unrestricted(), function(note) { %>\n      <% var title = dc.inflector.truncate(note.get(\'title\'), 30); %>\n      <option value="<%= note.id %>">p.<%= note.get(\'page\') %> &ndash; <%= title %></option>\n    <% }); %>\n  </select>\n<% } else { %>\n  <div class="error">\n    <%= _.t(\'document_has_no_public_notes\') %>\n  </div>\n<% } %>\n');
window.JST['document/page_images'] = _.template('<div class="page_list">\n  <div class="pages_header">\n    <span class="icon cancel_search" title="hide pages"></span>\n    <span class="pages_title interface noselect">\n      <% if (start > 1) { %>\n        <span class="arrow left">&larr;</span>\n      <% } %>\n      <% if (total > limit) { %>\n        <b><%= start %> &ndash; <%= end %></b> of\n      <% } %>\n      <% var totalLine = _.t(\'x_pages\', total) %>\n      <% if (total <= limit) { %>\n        <b class="pages_only"><%= totalLine %></b>\n      <% } else { %>\n        <%= totalLine %>\n      <% } %>\n      <% if (end < total) { %>\n        <span class="arrow right">&rarr;</span>\n      <% } %>\n    </span>\n    <span class="text_link open_pages noselect"><%= _.t(\'open_in_viewer\') %></span>\n  </div>\n  <% for (var page = start, l = end; page <= l; page++) { %>\n    <div class="page" data-page="<%= page %>">\n      <div class="page_icon">\n        <% if (notes[page]) { %>\n          <div class="icon mini_note"></div>\n        <% } %>\n        <img class="page_image" alt="" src="<%= doc.pageThumbnailURL(page, \'small\') %>" />\n        <div class="page_number"><%= _.t(\'pg\') %> <%= page %></div>\n      </div>\n    </div>\n  <% } %>\n  <div class="clear"></div>\n</div>\n');
window.JST['document/page_select'] = _.template('<select class="page_select">\n  <% for (var i = 1; i <= doc.get(\'page_count\'); i++) { %>\n    <option value="<%= i %>"><%= i %></option>\n  <% } %>\n</select>');
window.JST['document/pages'] = _.template('<%\n  var numbers = [];\n  var mentions = _.sortBy(_.flatten(doc.pageEntities.map(function(ent) {\n    return _.map(_.sortBy(ent.get(\'excerpts\'), function(obj) {\n      return obj.offset;\n    }), function(obj) {\n      numbers.push(obj.page_number);\n      return {id : ent.id, page_number : obj.page_number, excerpt : obj.excerpt, offset : obj.offset};\n    });\n  })), function(page) {\n    return page.page_number;\n  });\n  var pageCount = _.uniq(numbers).length;\n%>\n<% if (pageCount) { %>\n  <div class="pages_header">\n    <span class="icon cancel_search" title="hide pages"></span>\n    <span class="pages_title interface"><%= pageCount >= 200 ? _.t(\'over_x_mentions\', 200 ) : _.t(\'x_mentions\',mentions.length) %>\n    <%= _.t(\'of\') %> <%= dc.inflector.commify( doc.uniquePageEntityValues(), {quote : true, conjunction : _.t(\'or\') }) %>\n    <%= _.t(\'on\') %> <%= pageCount %>\n    <%= _.t(\'page\', pageCount ) %></span>\n  </div>\n  <% _.each(mentions, function(mention) { %>\n    <div class="page" data-page="<%= mention.page_number %>" data-id="<%= mention.id %>" data-offset="<%= mention.offset %>">\n      <div class="page_icon">\n        <img class="page_image" alt="page <%= mention.page_number %>" src="<%= doc.pageThumbnailURL(mention.page_number) %>" />\n        <div class="page_number"><%= _.t(\'pg\') %> <%= mention.page_number %></div>\n      </div>\n      <div class="page_excerpt noselect"><%= dc.inflector.trimExcerpt(mention.excerpt) %></div>\n      <div class="clear"></div>\n    </div>\n  <% }); %>\n  <div class="clear"></div>\n<% } %>\n');
