window.JST['workspace/paginator'] = _.template('<% var to = Math.min(q.to, q.total); %>\n<div class="description noselect">\n  <% if (q.total > per_page) { %>\n    <span class="range"><%= \'\' + (q.from + 1) %> &ndash; <%= to %></span>\n    of\n  <% } %>\n  <%= _.t(\'x_documents\', q.total) %>\n</div>\n\n<div class="sorter noselect quiet_link">\n  (<%= sort_text %>)\n</div>\n\n<div class="legal">\n  <a class="quiet_link" href="/terms"><%= _.t(\'terms\') %></a> &nbsp;&ndash;&nbsp;\n  <a class="quiet_link" href="/privacy"><%= _.t(\'privacy\') %></a>\n</div>\n\n<% if (q.total > per_page) { %>\n  <div class="noselect enumeration">\n    <% if (q.page > 1) { %>\n      <span class="arrow left">&larr;</span>\n    <% } %>\n      Page\n      <input type="text" class="current_page" value="<%= q.page %>" />\n      <span class="current_placeholder quiet_link"><%= q.page %></span> of <%= page_count %>\n    <% if (q.page < page_count) { %>\n      <span class="arrow right">&rarr;</span>\n    <% } %>\n  </div>\n<% } %>\n');
window.JST['workspace/panel'] = _.template('<div id="workspace_tabs" class="tabs">\n  <div id="search_tab" data-tab="search" class="tab active">\n    <%= _.t(\'documents\') %>\n    <div class="tab_corner left"></div>\n    <div class="tab_corner right"></div>\n  </div>\n  <% if (dc.account) { %>\n  <div id="accounts_tab" data-tab="account" class="tab trailing">\n    <%= _.t(\'accounts\') %>\n    <div class="tab_corner left"></div>\n    <div class="tab_corner right"></div>\n  </div>\n  <%} %>\n  <% if (dc.account && dc.account.isAdmin()) { %>\n  <div id="repositories_tab" data-tab="repo" class="tab trailing">\n    <%= _.t(\'repos\') %>\n    <div class="tab_corner left"></div>\n    <div class="tab_corner right"></div>\n  </div>\n  <% } %>\n  <% if (dc.account && dc.account.isAdmin()) { %>\n  <div id="templates_tab" data-tab="template" class="tab trailing">\n      <%= _.t(\'templates\') %>\n      <div class="tab_corner left"></div>\n      <div class="tab_corner right"></div>\n    </div>\n  <% } %>\n  <% if (dc.account && dc.account.canExtract()) { %>\n    <div id="extraction_tab" data-tab="template" class="tab trailing">\n        <%= _.t(\'extraction\') %>\n        <div class="tab_corner left"></div>\n        <div class="tab_corner right"></div>\n      </div>\n  <% } %>\n</div>\n\n<div class="panel_content">\n  <div class="search_tab_content">\n    <div class="title_box">\n      <span class="title_box_inner" class="noselect"></span>\n    </div>\n    <div id="search_box_container" class="active_search empty_search noselect"></div>\n    <div id="search_toolbar_container" class="active_search empty_search"></div>\n    <div id="document_list_container" class="active_search"></div>\n    <div id="no_results_container" class="empty_search"></div>\n    <div id="pagination_container" class="active_search"></div>\n  </div>\n  \n  <div id="accounts_manager_container" class="accounts_tab_content">\n  </div>\n\n  <div id="repo_manager_container" class="repositories_tab_content">\n  </div>\n\n  <div id="template_manager_container" class="templates_tab_content">\n  </div>\n\n  <div id="extraction_manager_container" class="extraction_tab_content">\n  </div>\n\n</div>\n');
window.JST['workspace/search_embed_dialog'] = _.template('<div class="publish_step publish_step_1 is_enabled">\n\n  <div class="publish_section publish_info selectable_text">\n    <%= _.t(\'x_public_documents\', publicCount ) %> \n    <%= projectQuery ? _.t(\'in_project\', publicCount ) : _.t(\'matching_search\', publicCount ) %>\n    <%= _.t(\'set_will_appear\') %>\n    <% if (restrictedCount > 0) {  %>\n      <span class="publish_section text_link change_access">\n      <%= _.t(\'x_private_documents\', restrictedCount ) %></span>\n      <%= _.t(\'no_appear_until_publish\', restrictedCount ) %>\n    <% } %>\n    <%= _.t(\'future_documents_w_appear\', projectQuery ? _.t(\'added_project\') : _.t(\'matching_search\',2) ) %>\n  </div>\n\n  <div class="publish_config">\n\n    <div class="publish_section">\n      <label for="publish_option_input_title"><%= _.t(\'title\') %>: </label>\n      <div class="text_input dark small">\n        <input id="publish_option_input_title" class="input_title" name="title" />\n      </div>\n      <span class="sidelabel">(<%= _.t(\'optional\') %>)</span>\n    </div>\n\n    <div class="publish_section">\n      <label for="publish_option_order"><%= _.t(\'order_documents_by\') %>:</label>\n      <select id="publish_option_order" class="embed_order" name="order">\n        <option value="title"><%= _.t(\'by_title\') %></option>\n        <option value="score"><%= _.t(\'by_relevance\') %></option>\n        <option value="created_at"><%= _.t(\'by_date\') %></option>\n        <option value="source"><%= _.t(\'by_source\') %></option>\n        <option value="page_count"><%= _.t(\'by_length\') %></option>\n      </select>\n    </div>\n\n    <div class="publish_section">\n      <label for="publish_option_per_page"><%= _.t(\'documents_per_page\') %>:</label>\n      <div class="text_input dark small">\n        <input id="publish_option_per_page" class="per_page" name="per_page" type="text" />\n      </div>\n      <span class="sidelabel publish_option_perpage_sidelabel"></span>\n    </div>\n\n    <div class="publish_section search_bar">\n      <label><%= _.t(\'search_bar\') %>:</label>\n      <input type="checkbox" name="search_bar" id="publish_option_search_bar" />\n      <label for="publish_option_search_bar" class="sidelabel"><%= _.t(\'allow_readers_to_search\') %>.</label>\n    </div>\n\n  </div>\n\n  <div class="publish_section">\n    <p class="publish_info selectable_text interface">\n      <%= _.t(\'preview_search_embed_help\', \'<span class="text_link preview">\', \'</span><br /><br />\' ) %>\n    </p>\n  </div>\n\n</div>\n\n<div class="publish_step publish_step_2">\n  <div class="publish_embed_code publish_section"></div>\n</div>\n\n\n<div class="publish_wizard_controls">\n  <div class="minibutton next default"><%= _.t(\'next\') %> <span class="text_arrow">&raquo;</span></div>\n  <div class="minibutton previous dark"><span class="text_arrow">&laquo;</span> <%= _.t(\'back\') %></div>\n  <div class="minibutton close dark"><%= _.t(\'close\') %></div>\n</div>\n');
window.JST['workspace/sidebar'] = _.template('<div id="sidebar_background"></div>\n<div id="sidebar_content">\n\n  <div id="organizer_container"></div>\n\n  <div id="entities_container">\n\n    <div id="entities" class="block">\n      <%= _.t(\'entities_unavailable\') %>\n      <br /><br />\n      <%= _.t(\'email_for_assistance\') %>\n    </div>\n\n  </div>\n\n  <div id="account_badge_container"></div>\n  <div id="organization_list_container"></div>\n\n</div>\n');
window.JST['workspace/toolbar'] = _.template('<div id="floating_toolbar">\n  <% if( dc.account.canOpenDocument() ){ %>\n    <div id="open_viewers" class="minibutton"><%= _.t(\'open\') %></div>\n  <% } %>\n  <div class="sort_menu_container"></div>\n  <% if( dc.account.isAdmin() ){ %>\n    <div class="edit_menu_container"></div>\n    <div class="project_menu_container"></div>\n    <div class="analyze_menu_container"></div>\n    <div class="publish_menu_container"></div>\n  <% } %>\n  <div id="size_toggle"></div>\n  <div id="floating_line"></div>\n</div>\n');
